namespace pins {
    /**
     * Get the size in bytes of specified number format.
     */
    //%
    export function sizeOf(format: NumberFormat) {
        switch (format) {
            case NumberFormat.Int8LE:
            case NumberFormat.UInt8LE:
            case NumberFormat.Int8BE:
            case NumberFormat.UInt8BE:
                return 1;
            case NumberFormat.Int16LE:
            case NumberFormat.UInt16LE:
            case NumberFormat.Int16BE:
            case NumberFormat.UInt16BE:
                return 2;
            case NumberFormat.Int32LE:
            case NumberFormat.Int32BE:
            case NumberFormat.UInt32BE:
            case NumberFormat.UInt32LE:
            case NumberFormat.Float32BE:
            case NumberFormat.Float32LE:
                return 4;
            case NumberFormat.Float64BE:
            case NumberFormat.Float64LE:
                return 8;
        }
        return 0;
    }

    /**
     * Create a new buffer initalized to bytes from given array.
     * @param bytes data to initalize with
     */
    //%
    export function createBufferFromArray(bytes: number[]) {
        let buf = control.createBuffer(bytes.length)
        for (let i = 0; i < bytes.length; ++i)
            buf[i] = bytes[i]
        return buf
    }

    function getFormat(pychar: string, isBig: boolean) {
        switch (pychar) {
            case 'B':
                return NumberFormat.UInt8LE
            case 'b':
                return NumberFormat.Int8LE
            case 'H':
                return isBig ? NumberFormat.UInt16BE : NumberFormat.UInt16LE
            case 'h':
                return isBig ? NumberFormat.Int16BE : NumberFormat.Int16LE
            case 'I':
            case 'L':
                return isBig ? NumberFormat.UInt32BE : NumberFormat.UInt32LE
            case 'i':
            case 'l':
                return isBig ? NumberFormat.Int32BE : NumberFormat.Int32LE
            case 'f':
                return isBig ? NumberFormat.Float32BE : NumberFormat.Float32LE
            case 'd':
                return isBig ? NumberFormat.Float64BE : NumberFormat.Float64LE
            default:
                return null as NumberFormat
        }
    }

    function packUnpackCore(format: string, nums: number[], buf: Buffer, isPack: boolean, off = 0) {
        let isBig = false
        let idx = 0
        for (let i = 0; i < format.length; ++i) {
            switch (format[i]) {
                case ' ':
                case '<':
                case '=':
                    isBig = false
                    break
                case '>':
                case '!':
                    isBig = true
                    break
                case 'x':
                    off++
                    break
                default:
                    let fmt = getFormat(format[i], isBig)
                    if (fmt === null) {
                        control.fail("Not supported format character: " + format[i])
                    } else {
                        if (buf) {
                            if (isPack)
                                buf.setNumber(fmt, off, nums[idx++])
                            else
                                nums.push(buf.getNumber(fmt, off))
                        }

                        off += pins.sizeOf(fmt)
                    }
                    break
            }
        }
        return off
    }

    export function packedSize(format: string) {
        return packUnpackCore(format, null, null, true)
    }

    export function packBuffer(format: string, nums: number[]) {
        let buf = control.createBuffer(packedSize(format))
        packUnpackCore(format, nums, buf, true)
        return buf
    }

    export function packIntoBuffer(format: string, buf: Buffer, offset: number, nums: number[]) {
        packUnpackCore(format, nums, buf, true, offset)
    }

    export function unpackBuffer(format: string, buf: Buffer, offset = 0) {
        let res: number[] = []
        packUnpackCore(format, res, buf, false, offset)
        return res
    }

    export function concatBuffers(bufs: Buffer[]) {
        let len = 0
        for (let b of bufs)
            len += b.length
        const r = control.createBuffer(len)
        len = 0
        for (let b of bufs) {
            r.write(len, b)
            len += b.length
        }
        return r
    }
}

// see http://msgpack.org/ for the spec
// it currently only implements numbers and their sequances
// once we handle any type and typeof expressions we can do more

namespace msgpack {
    function tagFormat(tag: number) {
        switch (tag) {
            case 0xCB: return NumberFormat.Float64BE
            case 0xCC: return NumberFormat.UInt8BE
            case 0xCD: return NumberFormat.UInt16BE
            case 0xCE: return NumberFormat.UInt32BE
            case 0xD0: return NumberFormat.Int8BE
            case 0xD1: return NumberFormat.Int16BE
            case 0xD2: return NumberFormat.Int32BE
            default:
                return null
        }
    }

    function packNumberCore(buf: Buffer, offset: number, num: number) {
        let tag = 0xCB
        if (num == (num << 0) || num == (num >>> 0)) {
            if (-31 <= num && num <= 127) {
                if (buf) buf[offset] = num
                return 1
            } else if (0 <= num) {
                if (num <= 0xff) {
                    tag = 0xCC
                } else if (num <= 0xffff) {
                    tag = 0xCD
                } else {
                    tag = 0xCE
                }
            } else {
                if (-0x7f <= num) {
                    tag = 0xD0
                } else if (-0x7fff <= num) {
                    tag = 0xD1
                } else {
                    tag = 0xD2
                }
            }
        }
        let fmt = tagFormat(tag)
        if (buf) {
            buf[offset] = tag
            buf.setNumber(fmt, offset + 1, num)
        }
        return pins.sizeOf(fmt) + 1
    }

    /**
     * Unpacks a buffer into a number array.
     */
    export function unpackNumberArray(buf: Buffer, offset = 0): number[] {
        let res: number[] = []

        while (offset < buf.length) {
            let fmt = tagFormat(buf[offset++])
            if (fmt === null) {
                let v = buf.getNumber(NumberFormat.Int8BE, offset - 1)
                if (-31 <= v && v <= 127)
                    res.push(v)
                else
                    return null
            } else {
                res.push(buf.getNumber(fmt, offset))
                offset += pins.sizeOf(fmt)
            }
            // padding at the end
            while (buf[offset] === 0xc1) offset++;
        }

        return res
    }

    /**
     * Pack a number array into a buffer.
     * @param nums the numbers to be packed
     */
    export function packNumberArray(nums: number[]): Buffer {
        let off = 0
        for (let n of nums) {
            off += packNumberCore(null, off, n)
        }
        let buf = control.createBuffer(off)
        off = 0
        for (let n of nums) {
            off += packNumberCore(buf, off, n)
        }
        return buf
    }
}

namespace helpers {
    export function bufferConcat(a: Buffer, b: Buffer) {
        const r = control.createBuffer(a.length + b.length)
        r.write(0, a)
        r.write(a.length, b)
        return r
    }

    export function bufferIndexOf(a: Buffer, b: Buffer) {
        for (let i = 0; i <= a.length - b.length; ++i) {
            if (a[i] == b[0]) {
                let j = 0
                while (j < b.length) {
                    if (a[i + j] != b[j])
                        break
                    j++
                }
                if (j >= b.length)
                    return i
            }
        }
        return -1
    }
}

interface Buffer {
    [index: number]: number;

    /**
     * Return concatenation of current buffer and the given buffer
     */
    //% helper=bufferConcat
    concat(other: Buffer): Buffer;

    /**
     * Return position of other buffer in current buffer
     */
    //% helper=bufferIndexOf
    indexOf(other: Buffer): number;

    // rest defined in buffer.cpp
}

base.console.ts:
/// <reference no-default-lib="true"/>

enum ConsolePriority {
    Debug = 0,
    Log = 1,
    Warning = 2,
    Error = 3,
    Silent = 4
}

/**
 * Reading and writing data to the console output.
 */
//% weight=12 color=#002050 icon="\uf120"
//% advanced=true
namespace console {
    type Listener = (priority: ConsolePriority, text: string) => void;

    /**
     * Minimum priority to send messages to listeners
     */
    export let minPriority = ConsolePriority.Log;

    //% whenUsed
    const listeners: Listener[] = [
        function (priority: ConsolePriority, text: string) { control.__log(priority, text); }
    ];

    export function add(priority: ConsolePriority, message: any) {
        let text = inspect(message);
        if (priority < minPriority) return;
        // add new line
        text += "\n";
        // send to listeners
        for (let i = 0; i < listeners.length; ++i)
            listeners[i](priority, text);
    }

    export function debug(text: any) {
        add(ConsolePriority.Debug, text);
    }

    export function warn(text: any) {
        add(ConsolePriority.Warning, text);
    }

    export function error(text: any) {
        add(ConsolePriority.Error, text);
    }

    /**
     * Write a line of text to the console output.
     * @param value to send
     */
    //% weight=90
    //% help=console/log blockGap=8
    //% blockId=console_log block="console log $value"
    //% value.shadow=text
    export function log(value: any): void {
        add(ConsolePriority.Log, value);
    }

    /**
     * Write a name:value pair as a line of text to the console output.
     * @param name name of the value stream, eg: "x"
     * @param value to write
     */
    //% weight=88 blockGap=8
    //% help=console/log-value
    //% blockId=console_log_value block="console|log value %name|= %value"
    //% name.shadow=text
    export function logValue(name: any, value: number): void {
        log(name ? `${inspect(name)}: ${value}` : `${value}`)
    }

    /**
     * Convert any object or value to a string representation
     * @param obj value to be converted to a string
     * @param maxElements [optional] max number values in an object to include in output
     */
    export function inspect(obj: any, maxElements = 20): string {
        if (typeof obj == "string") {
            return obj;
        } else if (typeof obj == "number") {
            return "" + obj;
        } else if (Array.isArray(obj)) {
            const asArr = (obj as Array<string>);
            if (asArr.length <= maxElements) {
                return asArr.join(",");
            } else {
                return `${asArr.slice(0, maxElements).join(",")}...`;
            }
        } else {
            const asString = obj + "";
            if (asString != "[object Object]"
                && asString != "[Object]") { // on arcade at least, default toString is [Object] on hardware instead of standard
                return asString;
            }

            let keys = Object.keys(obj);
            const snipped = keys.length > maxElements;
            if (snipped) {
                keys = keys.slice(0, maxElements);
            }

            return `{${
                keys.reduce(
                    (prev, currKey) => prev + `\n    ${currKey}: ${obj[currKey]}`,
                    ""
                ) + (snipped ? "\n    ..." : "")
            }
}`;
        }
    }

    /**
     * Adds a listener for the log messages
     * @param listener
     */
    //%
    export function addListener(listener: (priority: ConsolePriority, text: string) => void) {
        if (!listener || listeners.indexOf(listener) > -1) return;
        listeners.push(listener);
    }

    /**
     * Removes a listener
     * @param listener
     */
    //%
    export function removeListener(listener: (priority: ConsolePriority, text: string) => void) {
        if (!listener) return;
        const i = listeners.indexOf(listener);
        if (i > -1)
            listeners.splice(i, 1);
    }
}

base.control.cpp:
#include "pxtbase.h"


namespace control {
    /**
    * Gets the number of milliseconds elapsed since power on.
    */
    //% help=control/millis weight=50
    //% blockId=control_running_time block="millis (ms)"
    int millis() {
        return current_time_ms();
    }

    /**
    * Gets current time in microseconds. Overflows every ~18 minutes.
    */
    //%
    int micros() {
        return current_time_us() & 0x3fffffff;
    }

    /**
    * Used internally
    */
    //%
    void internalOnEvent(int src, int value, Action handler, int flags = 16) {
        registerWithDal(src, value, handler, flags);
    }

    /**
     * Reset the device.
     */
    //% weight=30 async help=control/reset blockGap=8
    //% blockId="control_reset" block="reset"
    void reset() {
        target_reset();
    }

    /**
    * Block the current fiber for the given microseconds
    * @param micros number of micro-seconds to wait. eg: 4
    */
    //% help=control/wait-micros weight=29 async
    //% blockId="control_wait_us" block="wait (s)%micros"
    void waitMicros(int micros) {
        sleep_us(micros);
    }

    /**
     * Run other code in the parallel.
     */
    //% help=control/run-in-parallel handlerStatement=1
    //% blockId="control_run_in_parallel" block="run in parallel" blockGap=8
    void runInParallel(Action a) {
        pxt::runInParallel(a);
    }

    /**
    * Blocks the calling thread until the specified event is raised.
    */
    //% help=control/wait-for-event async
    //% blockId=control_wait_for_event block="wait for event|from %src|with value %value"
    void waitForEvent(int src, int value) {
        pxt::waitForEvent(src, value);
    }

    /**
    * Derive a unique, consistent serial number of this device from internal data.
    */
    //% blockId="control_device_serial_number" block="device serial number" weight=9
    //% help=control/device-serial-number
    int deviceSerialNumber() {
        uint64_t serial_num = pxt::getLongSerialNumber();
        return hash_fnv1a(&serial_num, sizeof(serial_num)) & 0x3fffffff;
    }

    /**
    * Derive a unique, consistent 64-bit serial number of this device from internal data.
    */
    //% blockId="control_device_long_serial_number" block="device long serial number" weight=9
    //% help=control/device-long-serial-number
    Buffer deviceLongSerialNumber() {
        uint64_t serial_num = pxt::getLongSerialNumber();
        return mkBuffer((uint8_t*)&serial_num, sizeof(uint64_t));
    }

    /**
    *
    */
    //%
    void __log(int prority, String text) {
        if (NULL == text) return;
        pxt::sendSerial(text->getUTF8Data(), text->getUTF8Size());
    }

    /**
     * Dump internal information about a value.
     */
    //%
    void dmesgValue(TValue v) {
        anyPrint(v);
    }

    /**
     * Force GC and dump basic information about heap.
     */
    //%
    void gc() {
        pxt::gc(1);
    }

    /**
     * Force GC and halt waiting for debugger to do a full heap dump.
     */
    //%
    void heapDump() {
        pxt::gc(2);
        target_panic(PANIC_HEAP_DUMPED);
    }


    /**
     * Set flags used when connecting an external debugger.
     */
    //%
    void setDebugFlags(int flags) {
        debugFlags = flags;
    }

    /**
     * Record a heap snapshot to debug memory leaks.
     */
    //%
    void heapSnapshot() {
        // only in JS backend for now
    }

    /**
     * Return true if profiling is enabled in the current build.
     */
    //%
    bool profilingEnabled() {
#ifdef PXT_PROFILE
        return true;
#else
        return false;
#endif
    }
}

base.control.ts:
/**
* Program controls and events.
*/
//% weight=90 color="#FF5722" icon="\uf110" advanced=true
namespace control {
    /**
     * Deprecated, use ``control.runInParallel`` instead.
     */
    //% deprecated=1 hidden=1 help=control/run-in-background blockAllowMultiple=1 afterOnStart=true
    //% blockId="control_run_in_background" block="run in background" blockGap=8 weight=0
    export function runInBackground(a: () => void) {
        control.runInParallel(a);
    }

    export const enum PXT_PANIC {
        CODAL_OOM = 20,
        GC_OOM = 21,
        GC_TOO_BIG_ALLOCATION = 22,
        CODAL_HEAP_ERROR = 30,
        CODAL_NULL_DEREFERENCE = 40,
        CODAL_USB_ERROR = 50,
        CODAL_HARDWARE_CONFIGURATION_ERROR = 90,

        INVALID_BINARY_HEADER = 901,
        OUT_OF_BOUNDS = 902,
        REF_DELETED = 903,
        SIZE = 904,
        INVALID_VTABLE = 905,
        INTERNAL_ERROR = 906,
        NO_SUCH_CONFIG = 907,
        NO_SUCH_PIN = 908,
        INVALID_ARGUMENT = 909,
        MEMORY_LIMIT_EXCEEDED = 910,
        SCREEN_ERROR = 911,
        MISSING_PROPERTY = 912,
        INVALID_IMAGE = 913,
        CALLED_FROM_ISR = 914,
        HEAP_DUMPED = 915,
        STACK_OVERFLOW = 916,
        BLOCKING_TO_STRING = 917,
        VM_ERROR = 918,
        SETTINGS_CLEARED = 920,
        SETTINGS_OVERLOAD = 921,
        SETTINGS_SECRET_MISSING = 922,
        DELETE_ON_CLASS = 923,

        CAST_FIRST = 980,
        CAST_FROM_UNDEFINED = 980,
        CAST_FROM_BOOLEAN = 981,
        CAST_FROM_NUMBER = 982,
        CAST_FROM_STRING = 983,
        CAST_FROM_OBJECT = 984,
        CAST_FROM_FUNCTION = 985,
        CAST_FROM_NULL = 989,

        UNHANDLED_EXCEPTION = 999,
    }
    /**
     * Display an error code and stop the program.
     * @param code an error number to display. eg: 5
     */
    //% help=control/panic weight=29
    //% blockId="control_panic" block="panic %code"
    //% shim=pxtrt::panic
    export function panic(code: number) { }

    /**
     * Enable profiling for current function.
     */
    //% shim=TD_NOOP shimArgument=perfCounter
    export function enablePerfCounter(name?: string) { }

    /**
     * Dump values of profiling performance counters.
     */
    //% shim=pxt::dumpPerfCounters
    export function dmesgPerfCounters() { }

    /**
     * Display an error code and stop the program when the assertion is `false`.
     */
    //% help=control/assert weight=30
    //% blockId="control_assert" block="assert %cond|with value %code"
    export function assert(cond: boolean, code: number) {
        if (!cond) {
            fail("Assertion failed, code=" + code)
        }
    }

    export function fail(message: string) {
        console.log("Fatal failure: ")
        console.log(message)
        dmesg(message)
        panic(108)
    }

    export class AnimationQueue {
        running: boolean;
        eventID: number;
        public interval: number;

        constructor() {
            this.running = false;
            this.eventID = control.allocateNotifyEvent();
            this.interval = 1;
        }

        /**
         * Runs 'render' in a loop until it returns false or the 'stop' function is called
         */
        runUntilDone(render: () => boolean) {
            const evid = this.eventID;

            // if other animation, wait for turn
            if (this.running)
                control.waitForEvent(DAL.DEVICE_ID_NOTIFY, evid);

            // check if the animation hasn't been cancelled since we've waiting
            if (this.isCancelled(evid))
                return;

            // run animation
            this.running = true;
            while (this.running
                && !this.isCancelled(evid)
                && render()) {
                pause(this.interval);
            }

            // check if the animation hasn't been cancelled since we've been waiting
            if (this.isCancelled(evid))
                return;

            // we're done
            this.running = false;
            // unblock 1 fiber
            control.raiseEvent(DAL.DEVICE_ID_NOTIFY_ONE, this.eventID);
        }

        isCancelled(evid: number) {
            return this.eventID !== evid;
        }

        /**
         * Cancels the current running animation and clears the queue
         */
        cancel() {
            if (this.running) {
                this.running = false;
                const evid = this.eventID;
                this.eventID = control.allocateNotifyEvent();
                // unblock fibers
                control.raiseEvent(DAL.DEVICE_ID_NOTIFY, evid);
            }
        }
    }

    class PollEvent {
        public eid: number;
        public vid: number;
        public start: number;
        public timeOut: number;
        public condition: () => boolean;
        public once: boolean;
        constructor(eid: number, vid: number, start: number, timeOut: number, condition: () => boolean, once: boolean) {
            this.eid = eid;
            this.vid = vid;
            this.start = start;
            this.timeOut = timeOut;
            this.condition = condition;
            this.once = once;
        }
    }

    let _pollEventQueue: PollEvent[] = undefined;

    function pollEvents() {
        while (_pollEventQueue.length > 0) {
            const now = control.millis();
            for (let i = 0; i < _pollEventQueue.length; ++i) {
                const ev = _pollEventQueue[i];
                if (ev.condition() || (ev.timeOut > 0 && now - ev.start > ev.timeOut)) {
                    control.raiseEvent(ev.eid, ev.vid);
                    if (ev.once) {
                        _pollEventQueue.splice(i, 1);
                        --i;
                    }
                }
            }
            pause(50);
        }
        // release fiber
        _pollEventQueue = undefined;
    }

    export function __queuePollEvent(timeOut: number, condition: () => boolean, handler: () => void) {
        const ev = new PollEvent(
            control.allocateNotifyEvent(),
            1,
            control.millis(),
            timeOut,
            condition,
            !handler
        );

        // start polling fiber if needed
        if (!_pollEventQueue) {
            _pollEventQueue = [ev];
            control.runInParallel(pollEvents);
        }
        else {
            // add to the queue
            _pollEventQueue.push(ev)
        }

        // register event
        if (handler)
            control.onEvent(ev.eid, ev.vid, handler);
        else // or wait
            control.waitForEvent(ev.eid, ev.vid);
    }

    //% shim=pxt::getConfig
    export declare function getConfigValue(key: int32, defl: int32): number;

    //% shim=pxt::programHash
    export declare function programHash(): number;

    //% shim=pxt::programName
    export declare function programName(): string;

    export enum IntervalMode {
        Interval,
        Timeout,
        Immediate
    }

    let _intervals: Interval[] = undefined;
    class Interval {

        id: number;
        func: () => void;
        delay: number;
        mode: IntervalMode;

        constructor(func: () => void, delay: number, mode: IntervalMode) {
            this.id = _intervals.length == 0
                ? 1 : _intervals[_intervals.length - 1].id + 1;
            this.func = func;
            this.delay = delay;
            this.mode = mode;
            _intervals.push(this);

            control.runInParallel(() => this.work());
        }

        work() {
            // execute
            switch (this.mode) {
                case IntervalMode.Immediate:
                case IntervalMode.Timeout:
                    if (this.delay > 0)
                        pause(this.delay); // timeout
                    if (this.delay >= 0) // immediate, timeout
                        this.func();
                    break;
                case IntervalMode.Interval:
                    while (this.delay > 0) {
                        pause(this.delay);
                        // might have been cancelled during this duration
                        if (this.delay > 0)
                            this.func();
                    }
                    break;
            }
            // remove from interval array
            _intervals.removeElement(this);
        }

        cancel() {
            this.delay = -1;
        }
    }

    export function setInterval(func: () => void, delay: number, mode: IntervalMode): number {
        if (!func || delay < 0) return 0;
        if (!_intervals) _intervals = [];
        const interval = new Interval(func, delay, mode);
        return interval.id;
    }

    export function clearInterval(intervalId: number, mode: IntervalMode): void {
        if (!_intervals) return;
        for (let i = 0; i < _intervals.length; ++i) {
            const it = _intervals[i];
            if (it.id == intervalId && it.mode == mode) {
                it.cancel();
                break;
            }
        }
    }

    //% shim=control::_ramSize
    function _ramSize() {
        return 32 * 1024 * 1024;
    }

    /** Returns estimated size of memory in bytes. */
    export function ramSize() {
        return getConfigValue(DAL.CFG_RAM_BYTES, 0) || _ramSize();
    }

    /** Runs the function and returns run time in microseconds. */
    export function benchmark(f: () => void) {
        const t0 = micros()
        f()
        let t = micros() - t0
        if (t < 0)
            t += 0x3fffffff
        return t
    }

    //% shim=pxt::getGCStats
    function getGCStats(): Buffer {
        return null
    }

    export interface GCStats {
        numGC: number;
        numBlocks: number;
        totalBytes: number;
        lastFreeBytes: number;
        lastMaxBlockBytes: number;
        minFreeBytes: number;
    }

    /**
     * Get various statistics about the garbage collector (GC)
     */
    export function gcStats(): GCStats {
        const buf = getGCStats()
        if (!buf)
            return null
        let off = 0
        const res: any = {}

        addField("numGC")
        addField("numBlocks")
        addField("totalBytes")
        addField("lastFreeBytes")
        addField("lastMaxBlockBytes")
        addField("minFreeBytes")

        return res

        function addField(name: string) {
            res[name] = buf.getNumber(NumberFormat.UInt32LE, off)
            off += 4
        }
    }
}

/**
 * Busy wait for a condition to be true
 * @param condition condition to test for
 * @param timeOut if positive, maximum duration to wait for in milliseconds
 */
//% blockId="pxt_pause_until"
function pauseUntil(condition: () => boolean, timeOut?: number): void {
    if (!condition || condition()) return; // optimistic path
    if (!timeOut) timeOut = 0;
    control.__queuePollEvent(timeOut, condition, undefined);
}

/**
 * Convert any value to text
 * @param value value to be converted to text
 */
//% help=text/convert-to-text weight=1
//% block="convert $value=math_number to text"
//% blockId=variable_to_text blockNamespace="text"
function convertToText(value: any): string {
    return "" + value;
}

base.core.cpp:
#include "pxtbase.h"
#include <limits.h>
#include <stdlib.h>

using namespace std;

#define p10(v) __builtin_powi(10, v)

// try not to create cons-strings shorter than this
#define SHORT_CONCAT_STRING 50

// bigger value - less memory, but slower
// 16/20 keeps s.length and s.charCodeAt(i) at about 200 cycles (for actual unicode strings),
// which is similar to amortized allocation time
#define SKIP_INCR 16 // needs to be power of 2; needs to be kept in sync with compiler
#define MIN_SKIP 20  // min. size of string to use skip list; static code has its own limit

namespace pxt {

PXT_DEF_STRING(emptyString, "")

static HandlerBinding *handlerBindings;

HandlerBinding *nextBinding(HandlerBinding *curr, int source, int value) {
    for (auto p = curr; p; p = p->next) {
        // DEVICE_ID_ANY == DEVICE_EXT_ANY == 0
        if ((p->source == source || p->source == 0) && (value == -1 || p->value == value || p->value == 0)) {
            return p;
        }
    }
    return 0;
}

HandlerBinding *findBinding(int source, int value) {
    return nextBinding(handlerBindings, source, value);
}

void setBinding(int source, int value, Action act) {
    HandlerBinding *curr = NULL;
    for (auto p = handlerBindings; p; p = p->next) {
        if ((p->source == source) && (p->value == value)) {
            curr = p;
            break;
        }
    }
    if (curr) {
        curr->action = act;
        return;
    }
    curr = new (app_alloc(sizeof(HandlerBinding))) HandlerBinding();
    curr->next = handlerBindings;
    curr->source = source;
    curr->value = value;
    curr->action = act;
    registerGC(&curr->action);
    handlerBindings = curr;
}

void coreReset() {
    // these are allocated on GC heap, so they will go away together with the reset
    handlerBindings = NULL;
}

static const char emptyBuffer[] __attribute__((aligned(4))) = "@PXT#:\x00\x00\x00";

#if PXT_UTF8
int utf8Len(const char *data, int size) {
    int len = 0;
    for (int i = 0; i < size; ++i) {
        char c = data[i];
        len++;
        if ((c & 0x80) == 0x00) {
            // skip
        } else if ((c & 0xe0) == 0xc0) {
            i++;
        } else if ((c & 0xf0) == 0xe0) {
            i += 2;
        } else {
            // error; just skip
        }
    }
    return len;
}

const char *utf8Skip(const char *data, int size, int skip) {
    int len = 0;
    for (int i = 0; i <= size; ++i) {
        char c = data[i];
        len++;
        if (len > skip)
            return data + i;
        if ((c & 0x80) == 0x00) {
            // skip
        } else if ((c & 0xe0) == 0xc0) {
            i++;
        } else if ((c & 0xf0) == 0xe0) {
            i += 2;
        } else {
            // error; just skip over
        }
    }
    return NULL;
}

static char *write3byte(char *dst, uint32_t charCode) {
    if (dst) {
        *dst++ = 0xe0 | (charCode >> 12);
        *dst++ = 0x80 | (0x3f & (charCode >> 6));
        *dst++ = 0x80 | (0x3f & (charCode >> 0));
    }
    return dst;
}

static char *write2byte(char *dst, uint32_t charCode) {
    if (dst) {
        *dst++ = 0xc0 | (charCode >> 6);
        *dst++ = 0x80 | (0x3f & charCode);
    }
    return dst;
}

static int utf8canon(char *dst, const char *data, int size) {
    int outsz = 0;
    for (int i = 0; i < size;) {
        uint8_t c = data[i];
        uint32_t charCode = c;
        if ((c & 0x80) == 0x00) {
            charCode = c;
            i++;
        } else if ((c & 0xe0) == 0xc0 && i + 1 < size && (data[i + 1] & 0xc0) == 0x80) {
            charCode = ((c & 0x1f) << 6) | (data[i + 1] & 0x3f);
            if (charCode < 0x80)
                goto error;
            else
                i += 2;
        } else if ((c & 0xf0) == 0xe0 && i + 2 < size && (data[i + 1] & 0xc0) == 0x80 &&
                   (data[i + 2] & 0xc0) == 0x80) {
            charCode = ((c & 0x0f) << 12) | (data[i + 1] & 0x3f) << 6 | (data[i + 2] & 0x3f);
            // don't exclude surrogate pairs, since we're generating them
            if (charCode < 0x800 /*|| (0xd800 <= charCode && charCode <= 0xdfff)*/)
                goto error;
            else
                i += 3;
        } else if ((c & 0xf8) == 0xf0 && i + 3 < size && (data[i + 1] & 0xc0) == 0x80 &&
                   (data[i + 2] & 0xc0) == 0x80 && (data[i + 3] & 0xc0) == 0x80) {
            charCode = ((c & 0x07) << 18) | (data[i + 1] & 0x3f) << 12 | (data[i + 2] & 0x3f) << 6 |
                       (data[i + 3] & 0x3f);
            if (charCode < 0x10000 || charCode > 0x10ffff)
                goto error;
            else
                i += 4;
        } else {
            goto error;
        }

        if (charCode < 0x80) {
            outsz += 1;
            if (dst)
                *dst++ = charCode;
        } else if (charCode < 0x800) {
            outsz += 2;
            dst = write2byte(dst, charCode);
        } else if (charCode < 0x10000) {
            outsz += 3;
            dst = write3byte(dst, charCode);
        } else {
            outsz += 6; // a surrogate pair
            charCode -= 0x10000;
            dst = write3byte(dst, 0xd800 + (charCode >> 10));
            dst = write3byte(dst, 0xdc00 + (charCode & 0x3ff));
        }

        continue;

    error:
        i++;
        outsz += 2;
        dst = write2byte(dst, c);
    }
    return outsz;
}

static int utf8CharCode(const char *data) {
    unsigned char c = *data;
    if ((c & 0x80) == 0) {
        return c;
    } else if ((c & 0xe0) == 0xc0) {
        return ((c & 0x1f) << 6) | (data[1] & 0x3f);
    } else if ((c & 0xf0) == 0xe0) {
        return ((c & 0x0f) << 12) | (data[1] & 0x3f) << 6 | (data[2] & 0x3f);
    } else {
        return c; // error
    }
}

static bool isUTF8(const char *data, int len) {
    for (int i = 0; i < len; ++i) {
        if (data[i] & 0x80)
            return true;
    }
    return false;
}

#define NUM_SKIP_ENTRIES(p) ((p)->skip.length / SKIP_INCR)
#define SKIP_DATA(p) (const char *)(p->skip.list + NUM_SKIP_ENTRIES(p))

static void setupSkipList(String r, const char *data) {
    char *dst = (char *)SKIP_DATA(r);
    auto len = r->skip.size;
    if (data)
        memcpy(dst, data, len);
    dst[len] = 0;
    const char *ptr = dst;
    auto skipEntries = NUM_SKIP_ENTRIES(r);
    for (int i = 0; i < skipEntries; ++i) {
        ptr = utf8Skip(ptr, (int)(len - (ptr - dst)), SKIP_INCR);
        if (!ptr)
            oops(80);
        r->skip.list[i] = ptr - dst;
    }
}
#endif

#ifdef PXT_VM
String mkInternalString(const char *str) {
    int len = (int)strlen(str);
    String r = new (xmalloc(sizeof(void *) + 2 + len + 1)) BoxedString(&string_inline_ascii_vt);
    r->ascii.length = len;
    memcpy(r->ascii.data, str, len);
    r->ascii.data[len] = 0;
    return r;
}
#endif

String mkStringCore(const char *data, int len) {
    if (len < 0)
        len = (int)strlen(data);
    if (len == 0 && !inGCPrealloc())
        return (String)emptyString;

    auto vt = &string_inline_ascii_vt;
    String r;

#if PXT_UTF8
    if (data && isUTF8(data, len)) {
        vt = len >= MIN_SKIP ? &string_skiplist16_vt : &string_inline_utf8_vt;
    }
    if (vt == &string_skiplist16_vt) {
        r = new (gcAllocate(sizeof(void *) + sizeof(r->skip))) BoxedString(vt);
        r->skip.list = NULL;
        registerGCObj(r);
        r->skip.size = len;
        r->skip.length = utf8Len(data, len);
        r->skip.list = NULL; // in case gc triggers below
        r->skip.list = (uint16_t *)gcAllocateArray(NUM_SKIP_ENTRIES(r) * 2 + len + 1);
        setupSkipList(r, data);
        unregisterGCObj(r);
    } else
#endif
    {
        // for ASCII and UTF8 the layout is the same
        r = new (gcAllocate(sizeof(void *) + 2 + len + 1)) BoxedString(vt);
        r->ascii.length = len;
        if (data)
            memcpy(r->ascii.data, data, len);
        r->ascii.data[len] = 0;
    }

    MEMDBG("mkString: len=%d => %p", len, r);
    return r;
}

String mkString(const char *data, int len) {
#if PXT_UTF8
    if (len < 0)
        len = (int)strlen(data);
    if (len == 0 && !inGCPrealloc())
        return (String)emptyString;

    int sz = utf8canon(NULL, data, len);
    if (sz == len)
        return mkStringCore(data, len);
    // this could be optimized, but it only kicks in when the string isn't valid utf8
    // (or we need to introduce surrogate pairs) which is unlikely to be performance critical
    char *tmp = (char *)app_alloc(sz);
    utf8canon(tmp, data, len);
    auto r = mkStringCore(tmp, sz);
    app_free(tmp);
    return r;
#else
    return mkStringCore(data, len);
#endif
}

#if PXT_UTF8
// This converts surrogate pairs, which are encoded as 2 characters of 3 bytes each
// into a proper 4 byte utf-8 character.
uint32_t toRealUTF8(String str, uint8_t *dst) {
    auto src = str->getUTF8Data();
    auto len = str->getUTF8Size();
    auto dlen = 0;

    for (unsigned i = 0; i < len; ++i) {
        if ((uint8_t)src[i] == 0xED && i + 5 < len) {
            auto c0 = utf8CharCode(src + i);
            auto c1 = utf8CharCode(src + i + 3);
            if (0xd800 <= c0 && c0 < 0xdc00 && 0xdc00 <= c1 && c1 < 0xe000) {
                i += 5;
                auto charCode = ((c0 - 0xd800) << 10) + (c1 - 0xdc00) + 0x10000;
                if (dst) {
                    dst[dlen] = 0xf0 | (charCode >> 18);
                    dst[dlen + 1] = 0x80 | (0x3f & (charCode >> 12));
                    dst[dlen + 2] = 0x80 | (0x3f & (charCode >> 6));
                    dst[dlen + 3] = 0x80 | (0x3f & (charCode >> 0));
                }
                dlen += 4;
            }
        } else {
            if (dst)
                dst[dlen] = src[i];
            dlen++;
        }
    }
    return dlen;
}
#endif

Buffer mkBuffer(const uint8_t *data, int len) {
    if (len <= 0 && !inGCPrealloc())
        return (Buffer)emptyBuffer;
    Buffer r = new (gcAllocate(sizeof(BoxedBuffer) + len)) BoxedBuffer();
    r->length = len;
    if (data)
        memcpy(r->data, data, len);
    else
        memset(r->data, 0, len);
    MEMDBG("mkBuffer: len=%d => %p", len, r);
    return r;
}

static unsigned random_value = 0xC0DA1;

//%
void seedRandom(unsigned seed) {
    random_value = seed;
}

//%
void seedAddRandom(unsigned seed) {
    random_value ^= 0xCA2557CB * seed;
}

unsigned getRandom(unsigned max) {
    unsigned m, result;

    do {
        m = (unsigned)max;
        result = 0;

        do {
            // Cycle the LFSR (Linear Feedback Shift Register).
            // We use an optimal sequence with a period of 2^32-1, as defined by Bruce Schneier here
            // (a true legend in the field!),
            // For those interested, it's documented in his paper:
            // "Pseudo-Random Sequence Generator for 32-Bit CPUs: A fast, machine-independent
            // generator for 32-bit Microprocessors"
            // https://www.schneier.com/paper-pseudorandom-sequence.html
            unsigned r = random_value;

            r = ((((r >> 31) ^ (r >> 6) ^ (r >> 4) ^ (r >> 2) ^ (r >> 1) ^ r) & 1) << 31) |
                (r >> 1);

            random_value = r;

            result = ((result << 1) | (r & 0x00000001));
        } while (m >>= 1);
    } while (result > (unsigned)max);

    return result;
}

TNumber BoxedString::charCodeAt(int pos) {
#if PXT_UTF8
    auto ptr = this->getUTF8DataAt(pos);
    if (!ptr)
        return TAG_NAN;
    auto code = utf8CharCode(ptr);
    if (!code && ptr == this->getUTF8Data() + this->getUTF8Size())
        return TAG_NAN;
    return fromInt(code);
#else
    if (0 <= pos && pos < this->ascii.length) {
        return fromInt(this->ascii.data[pos]);
    } else {
        return TAG_NAN;
    }
#endif
}

