// ES5 9.5, 9.6
unsigned toUInt(TNumber v) {
    if (isInt(v))
        return numValue(v);
    if (isSpecial(v)) {
        if ((intptr_t)v >> 6)
            return 1;
        else
            return 0;
    }
    if (!v)
        return 0;

    NUMBER num = toDouble(v);
    if (!isnormal(num))
        return 0;
#ifdef PXT_USE_FLOAT
    float rem = fmodf(truncf(num), 4294967296.0);
#else
    double rem = fmod(trunc(num), 4294967296.0);
#endif
    if (rem < 0.0)
        rem += 4294967296.0;
    return (unsigned)rem;
}
int toInt(TNumber v) {
    return (int)toUInt(v);
}

NUMBER toDouble(TNumber v) {
    if (v == TAG_NAN || v == TAG_UNDEFINED)
        return NAN;
    if (isTagged(v))
        return toInt(v);

#ifdef PXT64
    if (isDouble(v))
        return doubleVal(v);
#endif

    ValType t = valType(v);

#ifndef PXT64
    if (t == ValType::Number) {
        BoxedNumber *p = (BoxedNumber *)v;
        return p->num;
    }
#endif

    if (t == ValType::String) {
        // TODO avoid allocation
        auto tmp = String_::toNumber((String)v);
        auto r = toDouble(tmp);
        return r;
    } else {
        return NAN;
    }
}

float toFloat(TNumber v) {
