    DEF_VTABLE(name##_vt, BoxedString, ValType::String, (void *)&dtorDoNothing, (void *)&anyPrint, \
               (void *)&name##_gcscan, (void *)&name##_gcsize, (void *)&name##_data,               \
               (void *)&name##_utfsize, (void *)&name##_length, (void *)&name##_dataAt)

