// ES5 9.5, 9.6
unsigned toUInt(TNumber v) {
    if (isInt(v))
        return numValue(v);
    if (isSpecial(v)) {
        if ((intptr_t)v >> 6)
            return 1;
        else
            return 0;
    }
    if (!v)
        return 0;

    NUMBER num = toDouble(v);
    if (!isnormal(num))
        return 0;
#ifdef PXT_USE_FLOAT
    float rem = fmodf(truncf(num), 4294967296.0);
#else
    double rem = fmod(trunc(num), 4294967296.0);
#endif
    if (rem < 0.0)
        rem += 4294967296.0;
    return (unsigned)rem;
}
int toInt(TNumber v) {
    return (int)toUInt(v);
}

NUMBER toDouble(TNumber v) {
    if (v == TAG_NAN || v == TAG_UNDEFINED)
        return NAN;
    if (isTagged(v))
        return toInt(v);

#ifdef PXT64
    if (isDouble(v))
        return doubleVal(v);
#endif

    ValType t = valType(v);

#ifndef PXT64
    if (t == ValType::Number) {
        BoxedNumber *p = (BoxedNumber *)v;
        return p->num;
    }
#endif

    if (t == ValType::String) {
        // TODO avoid allocation
        auto tmp = String_::toNumber((String)v);
        auto r = toDouble(tmp);
        return r;
    } else {
        return NAN;
    }
}

float toFloat(TNumber v) {
    // TODO optimize?
    return (float)toDouble(v);
}

#if !defined(PXT_HARD_FLOAT) && !defined(PXT_USE_FLOAT)
union NumberConv {
    double v;
    struct {
        uint32_t word0;
        uint32_t word1;
    };
};

static inline TValue doubleToInt(double x) {
    NumberConv cnv;
    cnv.v = x;

    if (cnv.word1 == 0 && cnv.word0 == 0)
        return TAG_NUMBER(0);

    auto ex = (int)((cnv.word1 << 1) >> 21) - 1023;

    // DMESG("v=%d/1000 %p %p %d", (int)(x * 1000), cnv.word0, cnv.word1, ex);

    if (ex < 0 || ex > 29) {
        // the 'MININT' case
        if (ex == 30 && cnv.word0 == 0 && cnv.word1 == 0xC1D00000)
            return (TValue)(0x80000001);
        return NULL;
    }

    int32_t r;

    if (ex <= 20) {
        if (cnv.word0)
            return TAG_UNDEFINED;
        if (cnv.word1 << (ex + 12))
            return TAG_UNDEFINED;
        r = ((cnv.word1 << 11) | 0x80000000) >> (20 - ex + 11);
    } else {
        if (cnv.word0 << (ex - 20))
            return TAG_UNDEFINED;
        r = ((cnv.word1 << 11) | 0x80000000) >> (20 - ex + 11);
        r |= cnv.word0 >> (32 - (ex - 20));
    }

    if (cnv.word1 >> 31)
        return TAG_NUMBER(-r);
    else
        return TAG_NUMBER(r);
}
#else
static inline TValue doubleToInt(NUMBER r) {
#ifdef PXT64
    if ((int)r == r)
        return TAG_NUMBER((int)r);
#else
    int ri = ((int)r) << 1;
    if ((ri >> 1) == r)
        return (TNumber)(uintptr_t)(ri | 1);
#endif
    return TAG_UNDEFINED;
}
#endif

TNumber fromDouble(NUMBER r) {
#ifndef PXT_BOX_DEBUG
    auto i = doubleToInt(r);
    if (i)
        return i;
#endif
    if (isnan(r))
        return TAG_NAN;
#ifdef PXT64
    return tvalueFromDouble(r);
#else
    BoxedNumber *p = NEW_GC(BoxedNumber);
    p->num = r;
    MEMDBG("mkNum: %d/1000 => %p", (int)(r * 1000), p);
    return (TNumber)p;
#endif
}

