        // https://www.ecma-international.org/ecma-262/6.0/#sec-string.prototype.split
        const A: string[] = [];
        let lim = 0;
        if (limit === undefined)
            lim = (1 << 29) - 1; // spec says 1 << 53, leaving it at 29 for constant folding
        else if (limit < 0)
            lim = 0;
        else
            lim = limit | 0;
        const s = S.length;
        let p = 0;
        const R = separator;
        if (lim == 0)
            return A;
        if (separator === undefined) {
            A[0] = S;
            return A;
        }
        if (s == 0) {
            let z = splitMatch(S, 0, R);
            if (z > -1) return A;
            A[0] = S;
            return A;
        }
        let T: string;
        let q = p;
        while (q != s) {
            let e = splitMatch(S, q, R);
            if (e < 0) q++;
            else {
                if (e == p) q++;
                else {
                    T = stringSlice(S, p, q);
                    A.push(T);
                    if (A.length == lim) return A;
                    p = e;
                    q = p;
                }
            }
        }
        T = stringSlice(S, p, q);
        A.push(T);
        return A;
    }

    function splitMatch(S: string, q: number, R: string): number {
        const r = R.length;
        const s = S.length;
        if (q + r > s) return -1;
        for (let i = 0; i < r; ++i) {
            if (S[q + i] != R[i])
                return -1;
        }
        return q + r;
    }

    export function stringEmpty(S: string): boolean {
        return !S;
    }
}

namespace Math {
    export function clamp(min: number, max: number, value: number): number {
        return Math.min(max, Math.max(min, value));
    }

    /**
      * Returns the absolute value of a number (the value without regard to whether it is positive or negative).
      * For example, the absolute value of -5 is the same as the absolute value of 5.
      * @param x A numeric expression for which the absolute value is needed.
      */
    export function abs(x: number): number {
        return x < 0 ? -x : x;
    }

    /**
      * Returns the sign of the x, indicating whether x is positive, negative or zero.
      * @param x The numeric expression to test
      */
    export function sign(x: number): number {
        if (x == 0) return 0;
        if (x > 0) return 1;
        return -1;
    }

    /**
      * Returns the larger of two supplied numeric expressions.
      */
    export function max(a: number, b: number): number {
        if (a >= b) return a;
        return b;
    }

    /**
      * Returns the smaller of two supplied numeric expressions.
      */
    export function min(a: number, b: number): number {
        if (a <= b) return a;
        return b;
    }

    /**
     * Rounds ``x`` to a number with the given number of ``digits``
     * @param x the number to round
     * @param digits the number of resulting digits
     */
    //%
    export function roundWithPrecision(x: number, digits: number): number {
        digits = digits | 0;
        // invalid digits input
        if (digits <= 0) return Math.round(x);
        if (x == 0) return 0;
        let r = 0;
        do {
            const d = Math.pow(10, digits);
            r = Math.round(x * d) / d;
            digits++;
        } while (r == 0 && digits < 21);
        return r;
    }
}


