namespace pins {

    let _spi: SPI;
    /**
    * Gets the default SPI driver
    */
    //%
    export function spi() {
        if (!_spi) {
            const mosi = pins.pinByCfg(DAL.CFG_PIN_MOSI);
            const miso = pins.pinByCfg(DAL.CFG_PIN_MISO);
            const sck = pins.pinByCfg(DAL.CFG_PIN_SCK);
