core---stm32.keyvaluestorage.ts:
namespace configStorage {
    /**
     * Stores the value at the key entry
     * @param key identifier of the key (max 16 characters)
     * @param value identifier of the value (max 32 characters)
     */
    export function setItem(key: string, value: string) {
        if (value == null)
            configStorage.removeItem(key);
        else
        {
            let idx = 0;
            let buf = control.createBuffer(value.length + 1);
            buf[idx++] = value.length;
            let valBuf = control.createBufferFromUTF8(value);

            for (let i = 0; i < valBuf.length; i++)
                buf[idx++] = valBuf[i];

            configStorage.setBuffer(key, buf);
        }
    }

    /**
     * Retrieves the value at the key entry
     * @param key identifier of the key (max 16 characters)
     */
    export function getItem(key: string): string {
        const buf = configStorage.getBuffer(key);

        if (!buf)
            return undefined;

        let idx = 0;
        let count = buf[idx++];
        const retBuf = control.createBuffer(count);

        for (let i = 0; i < count; i++)
            retBuf[i] = buf[idx++];

        return retBuf.toString();
    }
}

core---stm32.leveldetector.ts:
namespace pins {
    export class LevelDetector {
        public id: number;
        public min: number;
        public max: number;
        public lowThreshold: number;
        public highThreshold: number;
        private transition: number;
        private transitionMs: number;
        private _level: number;
        private _state: number;
        public onHigh: () => void;
        public onLow: () => void;
        public onNeutral: () => void;
        public transitionWindow: number;
        // minimum duration (ms) between events
        public transitionInterval: number;

        static LEVEL_THRESHOLD_NEUTRAL = 0;

        constructor(id: number,
            min: number, max: number,
            lowThreshold: number, highThreshold: number) {
            this.id = id;
            this.min = min;
            this.max = max;
            this.lowThreshold = lowThreshold;
            this.highThreshold = highThreshold;
            this.transitionWindow = 4;
            this.transitionInterval = 0;

            this.onHigh = () => control.raiseEvent(this.id, DAL.LEVEL_THRESHOLD_HIGH);
            this.onLow = () => control.raiseEvent(this.id, DAL.LEVEL_THRESHOLD_LOW);
            this.onNeutral = undefined;

            this.reset();
        }

        reset() {
            this.transition = 0;
            this.transitionMs = 0;
            this._level = Math.ceil((this.highThreshold - this.lowThreshold) / 2);
            this._state = LevelDetector.LEVEL_THRESHOLD_NEUTRAL;
        }

        get level(): number {
            return this._level;
        }

        set level(level: number) {
            this._level = this.clampValue(level);

            if (this._level >= this.highThreshold) {
                this.setState(DAL.LEVEL_THRESHOLD_HIGH);
            }
            else if (this._level <= this.lowThreshold) {
                this.setState(DAL.LEVEL_THRESHOLD_LOW);
            }
            else {
                this.setState(LevelDetector.LEVEL_THRESHOLD_NEUTRAL);
            }
        }

        public setLowThreshold(value: number) {
            this.lowThreshold = this.clampValue(value);
            this.reset();
        }

        public setHighThreshold(value: number) {
            this.highThreshold = this.clampValue(value);
            this.reset();
        }

        private clampValue(value: number) {
            if (value < this.min) {
                return this.min;
            }
            else if (value > this.max) {
                return this.max;
            }
            return value;
        }

        private setState(state: number) {
            // not enough samples to change
            if (this._state === state 
                || (this.transition++ < this.transitionWindow)
                || (control.millis() - this.transitionMs) < this.transitionInterval) {
                return;
            }

            this.transition = 0;
            this.transitionMs = control.millis();
            this._state = state;
            switch (state) {
                case DAL.LEVEL_THRESHOLD_HIGH:
                    if (this.onHigh) this.onHigh();
                    break;
                case DAL.LEVEL_THRESHOLD_LOW:
                    if (this.onLow) this.onLow();
                    break;
                case LevelDetector.LEVEL_THRESHOLD_NEUTRAL:
                    if (this.onNeutral) this.onNeutral();
                    break;
            }
        }
    }
}

core---stm32.light.cpp:
#include "light.h"

#if defined(SAMD21) || defined(SAMD51) || defined(STM32F4)
#include "neopixel.h"
#endif

#define NEOPIXEL_MIN_LENGTH_FOR_SPI 24
#define DOTSTAR_MIN_LENGTH_FOR_SPI 24

#define LIGHTMODE_RGB 1
#define LIGHTMODE_RGBW 2
#define LIGHTMODE_RGB_RGB 3
#define LIGHTMODE_DOTSTAR 4

namespace light {
bool isValidMOSIPin(DigitalInOutPin pin) {
    if (!pin)
        return false;

#if SAMD51
    return ZSPI::isValidMOSIPin(*pin);
#else
    // TODO: support for SPI neopixels
    // default SPI pins supported for now
    return pin == LOOKUP_PIN(MOSI);
#endif

}

// SPI
void spiNeopixelSendBuffer(DevicePin* pin, const uint8_t *data, unsigned size) {
    int32_t iptr = 0, optr = 100;
    uint32_t len = optr + size * 3 + optr;
    uint8_t *expBuf = new uint8_t[len];
    memset(expBuf, 0, len);
    uint8_t imask = 0x80;
    uint8_t omask = 0x80;

#define WR(k)                                                                                      \
    if (k)                                                                                         \
        expBuf[optr] |= omask;                                                                     \
    omask >>= 1;                                                                                   \
    if (!omask) {                                                                                  \
        omask = 0x80;                                                                              \
        optr++;                                                                                    \
    }

    while (iptr < (int)size) {
        WR(1);
        WR(data[iptr] & imask);
        imask >>= 1;
        if (!imask) {
            imask = 0x80;
            iptr++;
        }
        WR(0);
    }

    auto spi = pxt::getSPI(pin, NULL, NULL);
    spi->setFrequency(2400000);
    spi->transfer(expBuf, len, NULL, 0);
    delete expBuf;
}

void neopixelSendData(DevicePin* pin, int mode, const uint8_t* data, unsigned length) {
    if (!pin || !length) return;

#if defined(SAMD21) || defined(SAMD51) || defined(STM32F4)
    if (length > NEOPIXEL_MIN_LENGTH_FOR_SPI && isValidMOSIPin(pin))
        spiNeopixelSendBuffer(pin, data, length);
    else
        neopixel_send_buffer(*pin, data, length);
 #else
     if (isValidMOSIPin(pin)) {
         spiNeopixelSendBuffer(pin, data, length);
     }
 #endif
}

void bitBangDotStarSendData(DevicePin* data, DevicePin* clk, int mode, const uint8_t* buf, unsigned length) {
    // first frame of zeroes
    data->setDigitalValue(0);
    for (unsigned i = 0; i < 32; ++i) {
        clk->setDigitalValue(1);
        clk->setDigitalValue(0);
    }

    // data stream
    for (unsigned i = 0; i < length; ++i) {
        auto x = buf[i];
        for (uint8_t j = 0x80; j != 0; j >>= 1) {
            data->setDigitalValue(x & j ? 1 : 0);
            clk->setDigitalValue(1);
            clk->setDigitalValue(0);
        }
    }
    // https://cpldcpu.wordpress.com/2016/12/13/sk9822-a-clone-of-the-apa102/
    // reset frame
    //data->setDigitalValue(0);
    //for (unsigned i = 0; i < 32 ; ++i) {
    //    clk->setDigitalValue(1);
    //    clk->setDigitalValue(0);
    //}

    // https://cpldcpu.wordpress.com/2014/11/30/understanding-the-apa102-superled/
    data->setDigitalValue(1);
    unsigned n = 32;
    for (unsigned i = 0; i < n; ++i) {
        clk->setDigitalValue(1);
        clk->setDigitalValue(0);
    }
}

static uint8_t ZERO_FRAME[4];
static uint8_t ONE_FRAME[] = {1,1,1,1};
void spiDotStarSendData(DevicePin* data, DevicePin* clk, int mode, const uint8_t* buf, unsigned length) {
    auto spi = pxt::getSPI(data, NULL, clk);

    spi->transfer(ZERO_FRAME, sizeof(ZERO_FRAME), NULL, 0); // initial frame
    spi->transfer(buf, length, NULL, 0);
    spi->transfer(ZERO_FRAME, sizeof(ZERO_FRAME), NULL, 0); // reset frame
    for(unsigned i = 0; i < length >> 3; i += 32)
        spi->transfer(ONE_FRAME, sizeof(ONE_FRAME), NULL, 0); // final frame
}

void dotStarSendData(DevicePin* data, DevicePin* clk, int mode, const uint8_t* buf, unsigned length) {
    if (!data || !clk || !buf || !length) return;

    if (length > DOTSTAR_MIN_LENGTH_FOR_SPI && isValidMOSIPin(data))
        spiDotStarSendData(data, clk, mode, buf, length);
    else
        bitBangDotStarSendData(data, clk, mode, buf, length);
}

void sendBuffer(DevicePin* data, DevicePin* clk, int mode, Buffer buf) {
    if (!data || !buf || !buf->length) return;

    if (mode == LIGHTMODE_DOTSTAR)
        light::dotStarSendData(data, clk, mode, buf->data, buf->length);
    else
        light::neopixelSendData(data, mode, buf->data, buf->length);
}


void clear() {
    auto neopix = LOOKUP_PIN(NEOPIXEL);
    auto neonum = getConfig(CFG_NUM_NEOPIXELS, 0);
    if (neopix && neonum >= 0) {
        auto n = 3 * neonum;
        uint8_t off[n];
        memset(off, 0, sizeof(off));
        light::neopixelSendData(neopix, 0x100, off, sizeof(off));
    }

    auto data = LOOKUP_PIN(DOTSTAR_DATA);
    auto clk = LOOKUP_PIN(DOTSTAR_CLOCK);
    auto dsnum = getConfig(CFG_NUM_DOTSTARS, 0);
    if (data && clk && dsnum > 0) {
        auto n = 4 * dsnum;
        uint8_t off[n];
        memset(off, 0, sizeof(off));
        for(int i = 0; i < n; i += 4)
            off[i] = 0xe0;
        bitBangDotStarSendData(data, clk, 0x100, off, sizeof(off));
    }
}

} // namespace pxt

