namespace scene {
    export class Camera {
        // coordinate used for all physics computation
        protected _offsetX: number;
        protected _offsetY: number;

        // coordinate used for draw sprites, may including shaking
        drawOffsetX: number;
        drawOffsetY: number;
        sprite: Sprite;

        protected shakeStartTime: number;
        protected shakeDuration: number;
        protected shakeAmplitude: number;

        constructor() {
            this._offsetX = 0;
            this._offsetY = 0;

            this.drawOffsetX = 0;
            this.drawOffsetY = 0;
        }

        get offsetX() {
            return this._offsetX;
        }
        set offsetX(v: number) {
            const scene = game.currentScene();
            if (scene.tileMap && scene.tileMap.enabled) {
                this._offsetX = scene.tileMap.offsetX(v);
            } else {
                this._offsetX = v;
            }
        }
        get offsetY() {
            return this._offsetY;
        }
        set offsetY(v: number) {
            const scene = game.currentScene();
            if (scene.tileMap && scene.tileMap.enabled) {
                this._offsetY = scene.tileMap.offsetY(v);
            } else {
                this._offsetY = v;
            }
        }

        shake(amplitude: number = 4, duration: number = 1000) {
            if (amplitude <= 0 || duration <= 0) {
                this.shakeStartTime = undefined;
            } else {
                // this overrides any existing shake operation            
                this.shakeStartTime = control.millis();
                this.shakeAmplitude = amplitude;
                this.shakeDuration = duration;
                // don't reset offset, will be recomputed in update
            }
        }

        update() {
            // if sprite, follow sprite
            if (this.sprite) {
                this.offsetX = this.sprite.x - (screen.width >> 1);
                this.offsetY = this.sprite.y - (screen.height >> 1);
            }

            // normalize offset
            this.offsetX |= 0;
            this.offsetY |= 0;

            this.drawOffsetX = this.offsetX;
            this.drawOffsetY = this.offsetY;

            // apply shake if needed
            if (this.shakeStartTime !== undefined) {
                const elapsed = control.millis() - this.shakeStartTime;
                if (elapsed >= this.shakeDuration) {
                    // we are done!
                    this.shakeStartTime = undefined;
                } else {
                    // compute new shake
                    const percentComplete = elapsed / this.shakeDuration;
                    const dampStart = 0.75;
                    let damp = 1;
                    if (percentComplete >= dampStart)
                        damp = Math.max(0, 1 - percentComplete);
                    const f = this.shakeAmplitude * damp;
                    const x = (Math.random() * f) >> 0;
                    const y = (Math.random() * f) >> 0;
                    // apply to offset
                    this.drawOffsetX += x;
                    this.drawOffsetY += y;
                }
            }
        }
    }
}

game.console.ts:
namespace game.consoleOverlay {
    let consoleColor = 1;
    let consoleStrings: string[];
    let tabSize = 8;
    const marginx = 4;
    const marginy = 2;
    const consoleFont = image.font5;
    const consoleLines = Math.floor(screen.height / (consoleFont.charHeight + marginy)) - 1;
    const consoleColumns = Math.floor((screen.width - 2 * marginx) / consoleFont.charWidth);
    console.addListener(listener);

    export function isVisible() {
        return !!consoleStrings;
    }

    export function clear() {
        consoleStrings = [];
    }

    export function setVisible(value: boolean, col?: number) {
        if (value != !!consoleStrings)
            consoleStrings = value ? [] : undefined;
        if (col !== undefined)
            consoleColor = col;
    }

    function listener(priority: ConsolePriority, text: string) {
        if (!consoleStrings || !text)
            return;

        // split text into lines
        text.split("\n")
            .filter(line => !!line)
            .forEach(line => {
                for (let j = 0; j < line.length; j += consoleColumns) {
                    consoleStrings.push(line.slice(j, j + consoleColumns));
                }
            });

        if (consoleStrings.length > consoleLines) {
            consoleStrings.splice(0, consoleStrings.length - consoleLines);
        }
    }

    export function draw() {
        if (!consoleStrings || scene.systemMenu.isVisible()) return;
        const height = consoleFont.charHeight + marginy;
        const top = 2 + (game.stats ? height : 0);
        for (let i = 0; i < consoleStrings.length; ++i) {
            if (consoleStrings[i].indexOf("\t") >= 0) {
                const t = consoleStrings[i].split("\t");
                let tOff = 0;
                for (let tab of t) {
                    let padding = tabSize - ((tOff + tab.length) % tabSize)
                    screen.print(tab, marginx + (tOff * consoleFont.charWidth), top + i * height, consoleColor, consoleFont);
                    tOff += tab.length + padding;
                }
            }
            else
                screen.print(consoleStrings[i], marginx, top + i * height, consoleColor, consoleFont);
        }
    }
}

