namespace easing {
    export function linear(t: number): number { return t; }
    export function inQuad(t: number): number { return t * t; }
    export function outQuad(t: number): number { return t * (2 - t); }
    export function inOutQuad(t: number): number { return t < .5 ? 2 * t * t : -1 + (4 - 2 * t) * t; }
    export function inCubic(t: number): number { return t * t * t; }
    export function outCubic(t: number): number { return (--t) * t * t + 1; }
    export function inOutCubic(t: number): number { return t < .5 ? 4 * t * t * t : (t - 1) * (2 * t - 2) * (2 * t - 2) + 1; }
}

namespace light {
    export class BrightnessTransition {
        constructor() { }
        apply(strip: LightStrip, t: number, start: number, end: number): void {

        }
    }

    export class EasingBrightnessTransition extends BrightnessTransition {
        private timeEasing: (t: number) => number;
        private spatialEasing: (t: number) => number;

        constructor(
            timeEasing: (t: number) => number, 
            spatialEasing?: (t: number) => number) {
            super();
            this.timeEasing = timeEasing || easing.inOutQuad;
            this.spatialEasing = spatialEasing;
        }

        apply(strip: LightStrip, t: number, start: number, end: number): void {
            // t in [0..1]
            const db = end - start;
            const b = this.timeEasing(t); // [0..1]
            if (!this.spatialEasing) {
                strip.setBrightness(start + db * b);
            }
            else {
                // convolve desired brightness with spacial easing function
                const n = strip.length();
                for (let i = 0; i < n; ++i) {
                    const x = this.spatialEasing(i / (n - 1)); // [0..1]
                    strip.setPixelBrightness(i, end - db * (1 - b) * x);
                }
            }
        }
    }

    export class BrightnessTransitionPlayer {
        private transition: BrightnessTransition;
        private startBrightness: number;
        private endBrightness: number;
        private duration: number;
        private startTime: number;
        private repeat: number;
        private yoyo: number;

        constructor(
            transition: BrightnessTransition,
            startBrightness: number,
            endBrightness: number,
            duration: number,
            repeat: number,
            yoyo: boolean) {
            this.transition = transition;
            this.startBrightness = startBrightness;
            this.endBrightness = endBrightness;
            this.duration = duration;
            this.startTime = control.millis();
            this.repeat = repeat || 1;
            this.yoyo = yoyo ? 1 : 0;
        }

        update(strip: LightStrip): boolean {
            let elapsed = control.millis() - this.startTime;
            if (elapsed > this.duration) {
                this.yoyo = -this.yoyo;
                if (this.repeat > 0)
                    this.repeat--;
                this.startTime = control.millis();
                elapsed = 0;
                return this.repeat != 0;
            }

            let t = elapsed / this.duration;
            if (this.yoyo < 0)
                t = 1 - t;
            this.transition.apply(strip, t, this.startBrightness, this.endBrightness);
            return true;
        }
    }
}

tests.tests.ts:
/**
 * Various test event in the execution cycle
 */
enum TestEvent {
    //% block="run setup"
    RunSetUp = 0,
    //% block="run teardown"
    RunTearDown = 1,
    //% block="test setup"
    TestSetUp = 2,
    //% block="test teardown"
    TestTearDown = 3
}

/**
 * A Unit tests framework
 */
//% weight=100 color=#0fbc11 icon=""
namespace tests {
    class Test {
        name: string;
        handler: () => void;
        errors: string[];

        constructor(name: string, handler: () => void) {
            this.name = name;
            this.handler = handler;
            this.errors = [];
        }

        run() {
            // clear state
            if (_runSetup)
                _runSetup();

            console.log(`> ${this.name}`)
            this.handler()

            if (this.errors.length)
                console.log('')

            // ensure clean state after test
            if (_runTearDown)
                _runTearDown();
        }
    }

    let _tests: Test[] = undefined;
    let _currentTest: Test = undefined;
    let _runSetup: () => void = undefined;
    let _runTearDown: () => void = undefined;
    let _testSetUp: () => void = undefined;
    let _testTearDown: () => void = undefined;

    function run() {
        if (!_tests) return;

        if (_testSetUp)
            _testSetUp();

        const start = control.millis();
        console.log(`${_tests.length} tests found`)
        console.log(` `)
        for (let i = 0; i < _tests.length; ++i) {
            const t = _currentTest = _tests[i];
            t.run();
            _currentTest = undefined;
        }

        if (_testTearDown)
            _testTearDown();

        console.log(` `)
        console.log(`${_tests.length} tests, ${_tests.map(t => t.errors.length).reduce((p, c) => p + c, 0)} errs in ${Math.ceil((control.millis() - start) / 1000)}s`)
    }

    /**
     * Registers a test to run
     */
    //% blockId=testtest block="test %name"
    //% weight=100
    export function test(name: string, handler: () => void): void {
        if (!name || !handler) return;
        if (!_tests) {
            _tests = [];
            control.runInParallel(function () {
                // should run after on start
                pause(100)
                run()
            })
        }
        _tests.push(new Test(name, handler));
    }

    /**
     * Checks a boolean condition
     */
    //% blockId=testAssert block="assert %message|%condition"
    //% weight=80
    //% blockGap=8
    export function assert(message: string, condition: boolean) {
        if (!condition) {
            console.log(`!!! ${message || ''}`)
            if (_currentTest)
                _currentTest.errors.push(message);
        }
    }

    /**
     * Checks that 2 values are close to each other
     * @param expected what the value should be
     * @param actual what the value was
     * @param tolerance the acceptable error margin, eg: 5
     */
    //% blockId=testAssertClose block="assert %message|%expected|close to %actual|by %tolerance"
    //% weight=79
    //% inlineInputMode=inline
    export function assertClose(name: string, expected: number, actual: number, tolerance: number) {
        assert(`${name} ${expected} != ${actual} +-${tolerance}`, Math.abs(expected - actual) <= tolerance);
    }

    /**
     * Registers code to be called at various points in the test execution
     * @param handler
     */
    //% blockGap=8
    //% weight=10
    export function onEvent(event: TestEvent, handler: () => void) {
        switch(event) {
            case TestEvent.RunSetUp: _runSetup = handler; break;
            case TestEvent.RunTearDown: _runTearDown = handler; break;
            case TestEvent.TestSetUp: _testSetUp = handler; break;
            case TestEvent.TestTearDown: _testTearDown = handler; break;
        }
    }
}

