/// <reference path="service.ts"/>

namespace jacdac{
    const JD_CONTROL_SERVICE_STATUS_ENUMERATE = 0x02
    const JD_CONTROL_SERVICE_STATUS_ENUMERATING = 0x04
    const JD_CONTROL_SERVICE_STATUS_ENUMERATED = 0x08
    const JD_CONTROL_SERVICE_STATUS_BUS_LO = 0x10
    const JD_CONTROL_SERVICE_STATUS_INITIALISED = 0x20
    const JD_CONTROL_MAX_ALLOC_ADDRESS = 0xFE;
    const JD_CONTROL_MIN_ALLOC_ADDRESS = 1;
    export const JD_CONTROL_TRANSMIT_ONLY_ADDRESS = 0xFF

    export class JDControlService extends JDService {
        name: string
        deviceManager: JDDeviceManager;
        rngService: JDRNGService;
        configurationService: JDConfigurationService;
        _intervalId: any;
        nextAddress: number;

        onChange: () => void;

        send(buffer: Buffer): void {
            if (JACDAC.instance.bus.isConnected())
                JACDAC.instance.write(buffer, 0, 0, null);
        }

        deviceDisconnected(device: JDDevice): void {
            if (this.nextAddress == device.device_address)
                this.nextAddress = JD_CONTROL_MAX_ALLOC_ADDRESS;

            // iterate over services on the device and provide connect / disconnect events.
            for (let current of JACDAC.instance.services) {
                if (!current.device || current.mode == JDServiceMode.ControlLayerService || device.device_address != current.device.device_address)
                    continue;

                current.device = undefined;
                current.service_number = JD_SERVICE_NUMBER_UNITIALISED_VAL;
                current._hostDisconnected();
            }
        }

        deviceEnumerated(): void {
            // iterate over services on the device and provide connect / disconnect events.
            for (let current of JACDAC.instance.services) {
                if (current == this || current.mode == JDServiceMode.ClientService)
                    continue;

                current.device = this.device;
                current._hostConnected();
            }
        }

        formControlPacket(): JDControlPacket {
            const controlPacket = new JDControlPacket();

            controlPacket.unique_device_identifier = this.device.unique_device_identifier;
            controlPacket.device_address = this.device.device_address;
            controlPacket.device_flags = this.device.device_flags;
            controlPacket.device_name = this.name;

            let service_number = 0;

            for (let current of JACDAC.instance.services) {
                if (current.mode == JDServiceMode.ControlLayerService || current.mode == JDServiceMode.ClientService)
                    continue;

                if (current.service_number != JD_SERVICE_NUMBER_UNITIALISED_VAL && current.service_number != service_number)
                    jacdac.options.error("Device service order changed whilst enumerated");

                current.service_number = service_number;

                let info = new JDServiceInformation();

                info.service_flags = current.service_flags;
                info.service_class = current.service_class;
                const data = current.addAdvertisementData();
                info.advertisement_size = data.length;
                info.data = data;

                controlPacket.append(info.getBuffer());

                service_number++;
            }

            if (controlPacket.getBuffer().length > JD_SERIAL_MAX_PAYLOAD_SIZE)
                jacdac.options.error("Packet too big");

            return controlPacket;
        }

        /**
         * Timer callback every 500 ms
         **/
        startDeviceTimer() {

            if (this._intervalId !== undefined)
                jacdac.options.error("Device Timer called twice.");

            jacdac.options.log("start device timer")
            this._intervalId = setInterval(() => {
                jacdac.options.log("control tick")
                // handle enumeration
                if (this.status & JD_CONTROL_SERVICE_STATUS_ENUMERATE) {
                    if (this.status & JD_CONTROL_SERVICE_STATUS_ENUMERATING) {
                        this.device.rolling_counter++;

                        if (this.device.rolling_counter > 3) {
                            this.status &= ~JD_CONTROL_SERVICE_STATUS_ENUMERATING;
                            this.status |= JD_CONTROL_SERVICE_STATUS_ENUMERATED;
                            this.device.device_flags &= ~JD_DEVICE_FLAGS_PROPOSING;
                            this.deviceEnumerated();
                        }
                    }
                    else {
                        if (!JACDAC.instance.bus.isConnected()) {
                            this.device.rolling_counter++;

                            if (this.device.rolling_counter >= JD_CONTROL_ROLLING_TIMEOUT_VAL) {
                                this.status |= JD_CONTROL_SERVICE_STATUS_BUS_LO;
                                jacdac.options.log("This disconnected");
                                this.deviceDisconnected(this.device);
                                return;
                            }
                        }
                        else {
                            if (this.status & JD_CONTROL_SERVICE_STATUS_BUS_LO) {
                                this.deviceEnumerated();
                                this.status &= ~JD_CONTROL_SERVICE_STATUS_BUS_LO;
                            }

                            this.device.rolling_counter = 0;
                        }
                    }

                    // queue a control packet if we have host services.
                    this.send(this.formControlPacket().getBuffer());
                }

                // now check to see if remote devices have timed out.
                for (let dev of this.deviceManager.getDeviceList()) {
                    dev.rolling_counter++;

                    if (dev.rolling_counter >= JD_CONTROL_ROLLING_TIMEOUT_VAL) {
                        this.deviceManager.removeDevice(dev);
                        this.deviceDisconnected(dev);
                    }
                }
            }, 500);
        }

        private initialise(device_address?: number): void{
            if (!(this.status & JD_CONTROL_SERVICE_STATUS_INITIALISED))
            {
                this.status |= JD_CONTROL_SERVICE_STATUS_INITIALISED;
                JACDAC.instance.add(this.configurationService);
                JACDAC.instance.add(this.rngService);
                this.device = new JDDevice();
                this.device.unique_device_identifier = generate_eui64(jacdac.options.getSerialNumber());
            }

            // naiive implementation for now... we can sniff the bus for a little before enumerating to
            // get a good first address in the future.
            if (device_address)
                this.device.device_address = device_address;
            else
                this.device.device_address = random(JD_CONTROL_MIN_ALLOC_ADDRESS, JD_CONTROL_MAX_ALLOC_ADDRESS);

            jacdac.options.log(`device address: ${this.device.device_address}`)

            // set the device state for the control service.
            this.device.device_flags |= JD_DEVICE_FLAGS_PROPOSING;
            this.device.communication_rate = JD_DEVICE_DEFAULT_COMMUNICATION_RATE;
            this.device.rolling_counter = 0;
        }

        enumerate(device_address?: number): void {
            if (this.status & JD_CONTROL_SERVICE_STATUS_ENUMERATE) {
                jacdac.options.log("already enumerating")
                return;
            }

            this.initialise(device_address);

            let hostServiceCount = 0;

            for (let s of JACDAC.instance.services)
            {
                if (s.mode == JDServiceMode.ClientService || s.mode == JDServiceMode.ControlLayerService)
                    continue;

                hostServiceCount++;
            }

            if (hostServiceCount)
            {
                this.status |= (JD_CONTROL_SERVICE_STATUS_ENUMERATING | JD_CONTROL_SERVICE_STATUS_ENUMERATE);
                this.startDeviceTimer();
            }

            return;
        }

        /**
         *
         **/
        isEnumerated(): boolean {
            return (this.status & JD_CONTROL_SERVICE_STATUS_ENUMERATED) ? true : false;
        }

        /**
         *
         **/
        isEnumerating(): boolean {
            return (this.status & JD_CONTROL_SERVICE_STATUS_ENUMERATING) ? true : false;
        }

        disconnect(): void {
            if (!(this.status & JD_CONTROL_SERVICE_STATUS_ENUMERATE))
                return;

            clearInterval(this._intervalId);
            this._intervalId = undefined;
            this.deviceDisconnected(this.device);
            this.status &= ~JD_CONTROL_SERVICE_STATUS_ENUMERATE;
            return;
        }

        constructor() {
            super(JDServiceClass.CONTROL, JDServiceMode.ControlLayerService);
            this.deviceManager = new JDDeviceManager();
            this.rngService = new JDRNGService();
            this.configurationService = new JDConfigurationService();
            this.nextAddress = JD_CONTROL_MAX_ALLOC_ADDRESS;
        }

        getRemoteDevice(device_address: number): JDDevice{
            return this.deviceManager.getRemoteDevice(device_address);
        }

        routePacket(pkt: JDPacket) {
            // transmit only
            if (this.device.device_address == JD_CONTROL_TRANSMIT_ONLY_ADDRESS)
                return;

            let device: JDDevice = null;

            if (pkt.device_address == JD_CONTROL_TRANSMIT_ONLY_ADDRESS)
                device = this.deviceManager.getRemoteDeviceUnique(JD_CONTROL_TRANSMIT_ONLY_ADDRESS, pkt.udidh, pkt.udidl)
            else if (this.device && pkt.device_address == this.device.device_address)
                device = this.device;
            else
                device = this.getRemoteDevice(pkt.device_address);

            // jacdac.options.log("RP: a " + pkt.device_address +  " sn " + pkt.service_number +  " crc " + pkt.crc);

            const crc = jd_crc(pkt, device); // include size and address in the checksum.

            let crcCheck: boolean = (crc == pkt.crc);

            // console.log("crc check: computed ", crc, "  received: ", pkt.crc);

            if (crcCheck) {
                if (!device)
                    this.handlePacket(pkt);
                else {
                    // map from device broadcast map to potentially the service number of one of our enumerated broadcast hosts
                    let host_service_number: number = -1;

                    if (device.servicemap_bitmsk & (1 << pkt.service_number)) {
                        host_service_number = device.broadcast_servicemap.getUint8(pkt.service_number);
                        device = this.device;
                    }

                    let broadcast: boolean = (host_service_number >= 0);

                    // we matched a broadcast host, route to all broadcast hosts on the device.
                    if (broadcast)
                    {
                        let broadcast_class: number = 0;

                        for (let service of JACDAC.instance.services) {

                            if (!service.device || service.mode == JDServiceMode.ClientService || service.mode == JDServiceMode.ControlLayerService)
                                continue;

                            if (service.device.device_address == device.device_address && service.service_number == host_service_number) {
                                jacdac.options.log("BROADCAST MATCH CL:" + service.service_class);
                                broadcast_class = service.service_class;
                                break;
                            }
                        }

                        for (let service of JACDAC.instance.services) {
                            if (!service.device || service.mode != JDServiceMode.BroadcastHostService)
                                continue;

                            if (service.service_class == broadcast_class) {
                                // break if DEVICE_OK is returned (indicates the packet has been handled)
                                if (service.handlePacket(pkt) == DEVICE_OK)
                                    break;
                            }
                        }
                    }
                    else
                    {
                        // route to client / host services
                        for (let service of JACDAC.instance.services) {

                            if (!service.device || service.mode == JDServiceMode.ControlLayerService)
                                continue;

                            // jacdac.options.log("ITER: " + service.service_class.toString());
                            if (service.device && service.device.device_address == device.device_address && service.service_number == pkt.service_number)
                                if (service.handlePacket(pkt) == DEVICE_OK)
                                    break;
                        }
                    }
                }
            }
        }

        handlePacket(pkt: JDPacket): number {
            if (pkt.service_number == this.rngService.service_number) {
                this.rngService.handlePacket(pkt);
                return DEVICE_OK;
            }

            if (pkt.service_number == this.configurationService.service_number) {
                this.configurationService.handlePacket(pkt);
                return DEVICE_OK;
            }

            let cp: JDControlPacket = new JDControlPacket(pkt);


            // jacdac.options.log("HP ControlPacket: ",cp, cp.getBuffer());

            // address collision check
            if (this.status & (JD_CONTROL_SERVICE_STATUS_ENUMERATING | JD_CONTROL_SERVICE_STATUS_ENUMERATED)) {

                // maintain a "pointer" to the next address
                if (cp.device_address > this.device.device_address && cp.device_address < this.nextAddress)
                    this.nextAddress = cp.device_address;

                if (this.device.device_address == cp.device_address) {
                    // a different device is using our address!!
                    if (!(this.device.udidl == cp.udidl && this.device.udidh == cp.udidh)) {
                        // if the device is proposing, we can reject (as per the spec)
                        if (cp.device_flags & JD_DEVICE_FLAGS_PROPOSING) {
                            // if we're proposing too, the remote device has won the address
                            if (this.device.device_flags & JD_DEVICE_FLAGS_PROPOSING) {
                                this.device.rolling_counter = 0;
                                this.device.device_address = random(JD_CONTROL_MIN_ALLOC_ADDRESS,JD_CONTROL_MAX_ALLOC_ADDRESS);
                            }
                            // if our address is established, reject the proposal
                            else {
                                let rejectCP = new JDControlPacket();

                                rejectCP.device_address = cp.device_address;

                                // propose a decent next address
                                if (this.nextAddress != this.device.device_address)
                                    rejectCP.device_address = this.device.device_address + random(1, (this.nextAddress - this.device.device_address));

                                rejectCP.unique_device_identifier = cp.unique_device_identifier;
                                rejectCP.device_flags = cp.device_flags | JD_DEVICE_FLAGS_REJECT;
                                this.send(rejectCP.getBuffer())
                                jacdac.options.log("ASK OTHER TO REASSIGN");
                            }

                            return DEVICE_OK; // no further processing required.
                        }
                    }
                    // someone has flagged a conflict with our device address, re-enumerate
                    else if (cp.device_flags & JD_DEVICE_FLAGS_REJECT) {
                        this.device.rolling_counter = 0;

                        // we may receive a new recommended address in the rejection
                        // detect and then subsequently use.
                        if (cp.device_address != this.device.device_address)
                            this.device.device_address = cp.device_address;
                        else
                            this.device.device_address = random(JD_CONTROL_MIN_ALLOC_ADDRESS, JD_CONTROL_MAX_ALLOC_ADDRESS);

                        return DEVICE_OK;
                    }
                }
            }

            // the device has not got a confirmed address (enumerating)... if there was a collision it would be handled above
            if (cp.device_flags & JD_DEVICE_FLAGS_PROPOSING)
                return DEVICE_OK;

            // if a service is relying on a remote device, the control service is maintaining the state.
            let remoteDevice: JDDevice = this.deviceManager.getRemoteDeviceUnique(cp.device_address, cp.udidh, cp.udidl);

            if (remoteDevice)
            {
                // jacdac.options.log("TRACKED REMOTE DEVICE ", remoteDevice);
                this.deviceManager.updateDevice(cp, pkt.communication_rate);
            }
            // else
                // jacdac.options.log ("UNTRACKED");

            // if here, address validation has completed successfully... process service information
            const dataPointer = cp.data;
            let service_number = 0;
            let dataIdx = 0;

            // jacdac.options.log("USDEV: a %d, s %d, c %d, i %d, t %c%c%c", this.device.device_address, this.device.unique_device_identifier, this.service_class, this.status & JD_SERVICE_STATUS_FLAGS_INITIALISED ? 1 : 0, this.mode == BroadcastHostService ? 'B' : ' ', this.mode == HostService ? 'H' : ' ', this.mode == ClientService ? 'C' : ' ');
            while (dataIdx < dataPointer.length) {
                let serviceInfo = new JDServiceInformation(dataPointer.slice(dataIdx, dataPointer.length));
                // jacdac.options.log("data idx ", dataIdx, " ", dataPointer.length, " service ", serviceInfo);

                for (let current of JACDAC.instance.services) {

                    if (current.mode == JDServiceMode.ControlLayerService)
                        continue;

                    const class_check = current.service_class == serviceInfo.service_class;

                    // if the service is running, route the packet.
                    if (current.status & JD_SERVICE_STATUS_FLAGS_INITIALISED) {
                        const address_check = current.device.device_address == cp.device_address && current.service_number == service_number;
                        const serial_check = cp.udidl == current.device.udidl && cp.udidh == current.device.udidh;

                        // this boolean is used to override stringent address checks (not needed for broadcast services as they receive all packets) to prevent code duplication
                        const broadcast_override = current.mode == JDServiceMode.BroadcastHostService;
                        // JD_DMESG("INITDSer", current.device.device_address, current.device.unique_device_identifier, current.service_class, current.status & JD_SERVICE_STATUS_FLAGS_INITIALISED ? 1 : 0, current.mode == BroadcastHostService ? 'B' : ' ', current.mode == HostService ? 'H' : ' ', current.mode == ClientService ? 'C' : ' ');

                        // check if applicable
                        if ((address_check && serial_check && class_check) || (class_check && broadcast_override)) {
                            // we are receiving a packet from a remote device for a service in broadcast mode.
                            if (broadcast_override && cp.device_address != this.device.device_address) {
                                // create a device representation if none exists
                                if (!remoteDevice)
                                    remoteDevice = this.deviceManager.addDevice(cp, pkt.communication_rate);

                                remoteDevice.servicemap_bitmsk |= 1 << service_number;
                                remoteDevice.broadcast_servicemap.setUint8(service_number, current.service_number);
                            }

                            // if the service has handled the packet it will return DEVICE_OK.
                            // any non zero return value will cause packet routing to continue
                            if (current.handleServiceInformation(remoteDevice, serviceInfo) == DEVICE_OK) {
                                jacdac.options.log("uS ABSORBED " + current.device.device_address + " " + current.service_class);
                                break;
                            }
                        }
                    }
                    else if (class_check && current.mode == JDServiceMode.ClientService) {
                        // this service instance is looking for a specific device (either a unique_device_identifier or name)
                        if (current.requiredDevice) {
                            if (current.requiredDevice.unique_device_identifier
                                && current.requiredDevice.unique_device_identifier.length
                                && !(current.requiredDevice.udidl == cp.udidl && current.requiredDevice.udidh == cp.udidh))
                                continue;

                            if (current.requiredDevice.device_name && current.requiredDevice.device_name != cp.device_name)
                                continue;
                        }

                        jacdac.options.log("FOUND NEW" + current.service_class);
                        remoteDevice = this.deviceManager.addDevice(cp, pkt.communication_rate);

                        if (current.handleServiceInformation(remoteDevice, serviceInfo) == DEVICE_OK) {
                            current.device = remoteDevice;
                            current.service_number = service_number;
                            current._hostConnected();

                            if (this.onChange)
                                this.onChange();
                            break;
                        }
                    }
                }
                service_number++;
                dataIdx += JD_SERVICE_INFO_HEADER_SIZE + serviceInfo.advertisement_size;
            }

            return DEVICE_OK;
        }

        setDeviceName(name:string)
        {
            return this.name = name;
        }

        getDeviceName() : string
        {
            return this.name;
        }
    }
}

jacdac.controldebugview.ts:
/// <reference path="debugview.ts"/>

namespace jacdac{

    export class ControlDebugView extends DebugView
    {
        constructor() {
            super("CONTROL", jacdac.JDServiceClass.CONTROL);
        }

        renderPacket(pkt:jacdac.JDPacket){

            // only render control packet for now
            if (pkt.service_number == 0)
            {
                const cp = new jacdac.JDControlPacket(pkt);
                const device = new jacdac.JDDevice(cp);

                let cpFlags = ""
                cpFlags += (device.device_flags & JD_DEVICE_FLAGS_NACK) ? "NACK | " : ""
                cpFlags += (device.device_flags & JD_DEVICE_FLAGS_HAS_NAME) ? "NAME | " : ""
                cpFlags += (device.device_flags & JD_DEVICE_FLAGS_PROPOSING) ? "PROP | " : ""
                cpFlags += (device.device_flags & JD_DEVICE_FLAGS_REJECT) ? "REG | " : ""

                if (cpFlags.length == 0)
                    cpFlags = "NONE"

                let ss = "\r\nclass\tflags\tadvert\r\n"
                for (let s of device.services) {
                    ss += `${s.service_class}\t${s.service_flags}\t${s.data.toHex()}\r\n`
                }

                return `\r\nid: ${(device.device_name) ? device.device_name : device.udid.toHex()}\r\naddress: ${device.device_address}\tflags:${cpFlags} ${ss}\r\n`
            }

            return "";
        }
    }

}

jacdac.controllerclient.ts:
namespace jacdac {
    //% fixedInstances
    export class ControllerClient extends Broadcast {
        state: Buffer;
        streamingState: jacdac.SensorState;
        streamingInterval: number;
        stateUpdateHandler: () => void;
        lastServerTime: number;

        constructor() {
            super("ctrl", jacdac.CONTROLLER_DEVICE_CLASS, 3);
            this.controlData[0] = JDControllerCommand.ControlClient;
            this.serverAddress = 0;
            this.playerIndex = 0;
            this.state = control.createBuffer(2);
            this.state[0] = JDControllerCommand.ClientButtons;
            this.streamingState = jacdac.SensorState.Stopped;
            this.streamingInterval = 25;
            this.lastServerTime = 0;
        }

        get serverAddress() {
            return this.controlData[1];
        }

        set serverAddress(value: number) {
            this.controlData[1] = value;
        }

        get playerIndex(): number {
            return this.controlData[2];
        }

        set playerIndex(index: number) {
            this.controlData[2] = index;
        }

        isPressed(offset: JDControllerButton): boolean {
            const msk = 1 << offset;
            return !!(this.state[1] & msk);
        }

        setIsPressed(offset: JDControllerButton, down: boolean) {
            const b = this.state[1];
            const msk = 1 << offset;
            this.state[1] = down ? (b | msk) : (~(~b | msk));
        }

        //% blockCombine blockCombineShadow=toggleOnOff block="left is pressed" blockSetVariable="button"
        //% group="Controller"
        get leftIsPressed() {
            return this.isPressed(JDControllerButton.Left);
        }

        //% blockCombine
        //% group="Controller"
        set leftIsPressed(value: boolean) {
            this.setIsPressed(JDControllerButton.Left, value);
        }

        //% blockCombine block="right is pressed"
        //% group="Controller"
        get rightIsPressed() {
            return this.isPressed(JDControllerButton.Right);
        }

        //% blockCombine
        //% group="Controller"
        set rightIsPressed(value: boolean) {
            this.setIsPressed(JDControllerButton.Right, value);
        }

        //% blockCombine block="up is pressed"
        //% group="Controller"
        get upIsPressed() {
            return this.isPressed(JDControllerButton.Up);
        }

        //% blockCombine
        //% group="Controller"
        set upIsPressed(value: boolean) {
            this.setIsPressed(JDControllerButton.Up, value);
        }

        //% blockCombine block="down is pressed"
        //% group="Controller"
        get downIsPressed() {
            return this.isPressed(JDControllerButton.Down);
        }

        //% blockCombine
        //% group="Controller"
        set downIsPressed(value: boolean) {
            this.setIsPressed(JDControllerButton.Down, value);
        }

        //% blockCombine block="A is pressed"
        //% group="Controller"
        get AIsPressed() {
            return this.isPressed(JDControllerButton.A);
        }

        //% blockCombine
        //% group="Controller"
        set AIsPressed(value: boolean) {
            this.setIsPressed(JDControllerButton.A, value);
        }

        //% blockCombine block="B is pressed"
        //% group="Controller"
        get BIsPressed() {
            return this.isPressed(JDControllerButton.B);
        }

        //% blockCombine
        //% group="Controller"
        set BIsPressed(value: boolean) {
            this.setIsPressed(JDControllerButton.B, value);
        }

        /**
         * Register code to run when the state is about to be sent
         * @param handler 
         */
        //% blockId=jdctrlclientonstate block="on %controller state update"
        //% group="Controller"
        onStateUpdate(handler: () => void) {
            this.stateUpdateHandler = handler;
        }

        isActive(): boolean {
            return !!this.serverAddress && this.isConnected();
        }

        handleServiceInformation(device: JDDevice, serviceInfo: JDServiceInformation): number {
            const data = serviceInfo.data;
            return this.processPacket(device.device_address, data);
        }

        handlePacket(packet: JDPacket): number {
            const data = packet.data;
            return this.processPacket(packet.device_address, data);
        }

        private processPacket(packetAddress: number, data: Buffer): number {
            const cmd: JDControllerCommand = data[0];
            // received a packet from the server
            if (cmd == JDControllerCommand.ControlServer) {
                this.log(`server ${toHex8(packetAddress)}`)
                const address = this.deviceAddress;
                for (let i = 1; i <= 4; ++i) {
                    if (data[i] == address) {
                        // check that we are still connected to the same server
                        if (this.serverAddress != packetAddress) {
                            this.serverAddress = packetAddress;
                            this.playerIndex = i;
                            this.log(`server ${toHex8(this.serverAddress)}`);
                        }
                        this.lastServerTime = control.millis();
                        // start streaming
                        this.startStreaming();
                        return jacdac.DEVICE_OK;
                    }
                }
                // did the server drop us
                if (address == this.serverAddress) {
                    this.log(`dropped`);
                    this.serverAddress = 0; // streaming will stop automatically
                    this.playerIndex = 0;
                    this.stopStreaming();
                }

                // nope, doesn't seem to be our server
                // do nothing
            }
            return jacdac.DEVICE_OK;
        }

        start() {
            super.start();
            this.startStreaming();
        }

        private startStreaming() {
            if (this.streamingState != SensorState.Stopped)
                return;

            this.log(`start`);
            this.streamingState = SensorState.Streaming;
            control.runInBackground(() => this.stream());
        }

        private stream() {
            while (this.streamingState == SensorState.Streaming) {
                // alllow handle to update state
                if (this.stateUpdateHandler)
                    this.stateUpdateHandler();
                // send state
                this.sendPacket(this.state);
                // waiting for a bit
                pause(this.streamingInterval);
                // check if server still alive
                if (control.millis() - this.lastServerTime > 1000) {
                    this.serverAddress = 0; // inactive
                }
            }
            this.streamingState = SensorState.Stopped;
            this.log(`stopped`);
        }

        private stopStreaming() {
            if (this.streamingState == SensorState.Streaming) {
                this.log(`stopping`)
                this.streamingState = SensorState.Stopping;
                pauseUntil(() => this.streamingState == SensorState.Stopped);
            }
        }
    }

    //% fixedInstance whenUsed block="controller client"
    export const controllerClient = new ControllerClient();
}

jacdac.crc.ts:
namespace jacdac{
    const JD_CRC_POLYNOMIAL = 0xF13;

    export function jd_crc(pkt: JDPacket, device?: JDDevice): number {
        let crc: number = 0xfff;
        if (device) {
            const udidPtr = device.unique_device_identifier;
            let i: number = 0;

            while (i < 8) {
                crc ^= (udidPtr.getUint8(i) << 8);
                for (let j = 0; j < 8; ++j) {
                    if (crc & 0x800)
                        crc = (crc << 1) ^ JD_CRC_POLYNOMIAL;
                    else
                        crc = crc << 1;
                }

                i++;
            }
        }

        let len = pkt.size + JD_SERIAL_CRC_HEADER_SIZE + 2;
        let idx = JD_SERIAL_CRC_HEADER_SIZE;
        const buf = pkt.getBuffer();

        while (idx < len) {
            crc ^= (buf.getUint8(idx) << 8);
            for (let i = 0; i < 8; ++i) {
                if (crc & 0x800)
                    crc = (crc << 1) ^ JD_CRC_POLYNOMIAL;
                else
                    crc = crc << 1;
            }
            idx++;
        }

        return crc & 0xFFF;
    }
}

jacdac.debugservice.ts:
namespace jacdac{

    export interface DebuggerStruct
    {
        pkt: jacdac.JDPacket;
        view: DebugView;
        timestamp: number;
        crcStatus:string;
        serviceClass: string;
    }

    export enum DebuggerClassEnums {
        CONTROL = 0,
        CONTROL_RNG = 1,
        CONTROL_CONFIGURATION = 2,
        CONTROL_TEST = 3,

        JOYSTICK = 4,
        MESSAGE_BUS = 5,
        BRIDGE = 6,
        BUTTON = 7,
        ACCELEROMETER = 8,
        CONSOLE = 9
    }

    export class DebuggerService extends jacdac.JDService {
        running: boolean;
        deviceManager: jacdac.JDDeviceManager
        _intervalId: any;
        paintDevices:(devices: jacdac.JDDevice[]) => void;
        paintPacket:(packet: DebuggerStruct) => void;

        constructor(){
            super(jacdac.JDServiceClass.BRIDGE, jacdac.JDServiceMode.ClientService)
            jacdac.JACDAC.instance.bridge = this;
            this.running = true;
            this.deviceManager = new jacdac.JDDeviceManager();

            this._intervalId = setInterval(()=>{
                const devices = this.deviceManager.getDeviceList();

                for (let device of devices)
                {
                    device.rolling_counter++;

                    if (device.rolling_counter >= jacdac.JD_CONTROL_ROLLING_TIMEOUT_VAL)
                    {
                        this.deviceManager.removeDevice(device);

                        if (this.paintDevices)
                            this.paintDevices(this.deviceManager.getDeviceList());
                    }
                }
            },500)
        }

        handlePacket(pkt : jacdac.JDPacket) : number
        {
            // let packetEntry: DebuggerStruct ={pkt: pkt, view: undefined, timestamp:jacdac.options.getTimeMs(), crcStatus: "Poo", serviceClass: "ABC"}

            // if (this.paintPacket)
            //     this.paintPacket(packetEntry);

            // return jacdac.DEVICE_OK;

            if (!this.running)
                return jacdac.DEVICE_OK;

            // console.log("DEBUGGER HANDLE PACKET", pkt, pkt.device_address, pkt.service_number);
            if (pkt.device_address == 0 && pkt.service_number == 0)
            {
                const cp = new jacdac.JDControlPacket(pkt);
                // console.log(cp);

                if ((cp.device_flags & (jacdac.JD_DEVICE_FLAGS_REJECT | jacdac.JD_DEVICE_FLAGS_PROPOSING)) == 0)
                {
                    const remote = this.deviceManager.addDevice(cp, pkt.communication_rate);

                    if (this.paintDevices)
                        this.paintDevices(this.deviceManager.getDeviceList());

                    // console.log("CP REM ",remote);
                    remote.update(cp);
                }
                // console.log("propose ",cp.device_flags & (jacdac.JD_DEVICE_FLAGS_PROPOSING), " rejecting ",cp.device_flags & (jacdac.JD_DEVICE_FLAGS_REJECT) )
            }

            const remote = this.deviceManager.getRemoteDevice(pkt.device_address);

            // console.log("REMOTE ", pkt.device_address, pkt.service_number, remote, this.deviceManager);
            const crc = jacdac.jd_crc(pkt,remote);

            let crcString = "OK"

            if (crc != pkt.crc)
                if (!remote)
                    crcString = "ERROR - DEVICE NOT FOUND";
                else
                    crcString = "ERROR - INCORRECT"

            let serviceString = "UNKNOWN";

            let view = undefined;
            if (remote)
            {
                let serviceClass = remote.services[pkt.service_number].service_class;
                // serviceString =  DebuggerClassEnums[serviceClass] || serviceClass.toString();
                view = DebugView.find(serviceClass);
            }
            else if (pkt.device_address == 0)
            {
                serviceString = "CONTROL";
                view = DebugView.find(jacdac.JDServiceClass.CONTROL);
            }

            let packetEntry ={pkt: pkt, view: view, timestamp:jacdac.options.getTimeMs(), crcStatus: crcString, serviceClass: serviceString}

            if (this.paintPacket)
                this.paintPacket(packetEntry)

            return jacdac.DEVICE_OK;
        }

        handleServiceInformation(device: jacdac.JDDevice, serviceInfo: jacdac.JDServiceInformation): number {
            return jacdac.DEVICE_OK;
        }
    }

}

