namespace jacdac {
    export class SensorDebugView extends DebugView {
        constructor(name: string, driverClass: number) {
            super(name, driverClass);
        }

        renderControlPacket(device: JDDevice, service_information: JDServiceInformation): string {
            const data = service_information.data;
            const state = data[0];
            switch (state) {
                case SensorState.Stopping: return "stopping";
                case SensorState.Streaming: return "stream";
                default: return "stop";
            }
        }

        renderPacket(packet: JDPacket): string {
            const data = packet.data;
            const cmd = data[0];
            switch (cmd) {
                case SensorCommand.StartStream:
                    const interval = data.getNumber(NumberFormat.UInt32LE, 1);
                    return `start stream ${interval ? `(${interval}ms)` : ''}`;
                case SensorCommand.StopStream:
                    return `stop stream`;
                case SensorCommand.LowThreshold:
                    return `low ${data[1]}`
                case SensorCommand.HighThreshold:
                    return `high ${data[1]}`
                case SensorCommand.Event:
                    return this.renderEvent(data[1]);
                case SensorCommand.State:
                    return `ev ${this.renderState(data.slice(1)) || data[1]}`;
                default:
                    return this.renderCustomPacket(cmd, packet);
            }
        }

        renderCustomPacket(cmd: number, packet: JDPacket): string {
            return packet.data.toHex();
        }

        renderEvent(value: number): string {
            return value.toString();
        }

        renderState(data: Buffer): string {
            return data.toHex();
        }
    }

    class LightDebugView extends DebugView {
        constructor() {
            super("light", jacdac.LIGHT_DEVICE_CLASS);
        }

        renderPacket(packet: JDPacket) {
            const data = packet.data;
            const cmd = data[0];
            const payload = data.slice(1, data.length - 1);
            const names = [
                "none",
                "all",
                "bright",
                "rainbow",
                "rlights",
                "colorw",
                "comet",
                "theaterchase",
                "sparkle"
            ];
            return `${names[cmd]} ${payload.toHex()}`
        }
    }

    class MusicDebugView extends DebugView {
        constructor() {
            super("music", jacdac.MUSIC_DEVICE_CLASS);
        }

        renderPacket(packet: JDPacket) {
            const data = packet.data;
            const cmd = data[0];
            switch (cmd) {
                case JDMusicCommand.PlayTone:
                    return `tone ${data.getNumber(NumberFormat.UInt32LE, 1)} ${data.getNumber(NumberFormat.UInt32LE, 5)}`;
                default:
                    return "";
            }
        }
    }

    class ConsoleDebugView extends DebugView {
        constructor() {
            super("log", jacdac.LOGGER_DEVICE_CLASS);
        }
        renderControlPacket(device: JDDevice, service_information: JDServiceInformation): string {
            const data = service_information.data;
            return `${["off", "broad", "listen"][data[0]]} ${data[1]}`;
        }

        renderPacket(packet: JDPacket) {
            const data = packet.data;
            const pri = data[0];
            const str = bufferToString(data, 1);
            return `${pri}:${str}`;
        }
    }

    class MessageBusDebugView extends DebugView {
        constructor() {
            super(MessageBusService.NAME, jacdac.MESSAGE_BUS_DEVICE_CLASS);
        }

        renderPacket(packet: JDPacket): string {
            const data = packet.data;
            return `${data.getNumber(NumberFormat.UInt16LE, 0)} ${data.getNumber(NumberFormat.UInt16LE, 2)}`;
        }
    }

    class AccelerometerDebugView extends SensorDebugView {
        constructor() {
            super("acc", jacdac.ACCELEROMETER_DEVICE_CLASS);
        }

        renderEvent(data: number): string {
            switch (data) {
                case JDGesture.Shake: return "shake";
                case JDGesture.FreeFall: return "freefall";
            }
            return "";
        }

        renderState(data: Buffer): string {
            const x = data.getNumber(NumberFormat.UInt16LE, 0);
            const y = data.getNumber(NumberFormat.UInt16LE, 2);
            const z = data.getNumber(NumberFormat.UInt16LE, 4);
            return `${x} ${y} ${z}`;
        }
    }

    class ButtonDebugView extends SensorDebugView {
        constructor() {
            super("button", jacdac.BUTTON_DEVICE_CLASS);
        }

        renderEvent(value: number): string {
            switch (value) {
                case JDButtonEvent.Click: return "click";
                case JDButtonEvent.Down: return "down";
                case JDButtonEvent.Up: return "up";
                case JDButtonEvent.LongClick: return "lg click"
                default: return "";
            }
        }

        renderState(data: Buffer): string {
            return !!data[0] ? `up` : `down`;
        }
    }

    class LightSensorDebugView extends SensorDebugView {
        constructor() {
            super("lis", jacdac.LIGHT_SENSOR_DEVICE_CLASS);
        }
    }

    class MicrophoneDebugView extends SensorDebugView {
        constructor() {
            super("mic", jacdac.MICROPHONE_DEVICE_CLASS);
        }

        renderEvent(value: number): string {
            if (value == DAL.SENSOR_THRESHOLD_HIGH)
                return "loud";
            return "";
        }

        renderState(data: Buffer): string {
            return `level ${data[0]}`;
        }
    }

    class SwitchDebugView extends SensorDebugView {
        constructor() {
            super("switch", jacdac.SWITCH_DEVICE_CLASS);
        }

        renderEvent(value: number): string {
            switch (value) {
                case JDSwitchDirection.Left: return "left";
                case JDSwitchDirection.Right: "right";
                default: return "";
            }
        }

        renderState(data: Buffer): string {
            return !!data[0] ? `right` : `left`;
        }
    }

    class ThermometerDebugView extends SensorDebugView {
        constructor() {
            super("temp", jacdac.THERMOMETER_DEVICE_CLASS);
        }
    }

    class TouchDebugView extends SensorDebugView {
        constructor() {
            super("touch", jacdac.TOUCHBUTTON_DEVICE_CLASS);
        }

        renderEvent(value: number): string {
            switch (value) {
                case JDButtonEvent.Click: return "click";
                case JDButtonEvent.Down: "down";
                case JDButtonEvent.Up: "up";
                case JDButtonEvent.LongClick: return "lg click"
                default: return "";
            }
        }

        renderState(data: Buffer): string {
            return `${data.getNumber(NumberFormat.UInt16LE, 0)}`;
        }
    }

    // class PixelDebugView extends ActuatorDebugView {
    //     constructor() {
    //         super("pixel", jacdac.PIXEL_DEVICE_CLASS);
    //     }
    // }

    class ControllerDebugView extends DebugView {
        constructor() {
            super("ctrl", jacdac.CONTROLLER_DEVICE_CLASS);
        }

        renderControlPacket(device: JDDevice, service_information: JDServiceInformation): string {
            const data = service_information.data;
            return this.renderData(data);
        }

        renderPacket(packet: JDPacket): string {
            const data = packet.data;
            return this.renderData(data);
        }

        private renderData(data: Buffer): string {
            const cmd: JDControllerCommand = data[0];
            switch (cmd) {
                case JDControllerCommand.ClientButtons:
                    const state = data[1];
                    const left = state & (1 << 1);
                    const up = state & (1 << 2);
                    const right = state & (1 << 3);
                    const down = state & (1 << 4);
                    const A = state & (1 << 5);
                    const B = state & (1 << 6);
                    return `${left ? "L" : "-"}${up ? "U" : "-"}${right ? "R" : "-"}${down ? "D" : "-"}${A ? "A" : "-"}${B ? "B" : "-"}`;
                case JDControllerCommand.ControlServer:
                    return `srv> ${data[1] ? toHex8(data[1]) : "--"} ${data[2] ? toHex8(data[2]) : "--"} ${data[3] ? toHex8(data[3]) : "--"} ${data[4] ? toHex8(data[4]) : "--"}`;
                case JDControllerCommand.ControlClient:
                    return `client> ${data[1] ? toHex8(data[1]) : "--"}`;
                default:
                    return toHex8(cmd);
            }
        }
    }

    export function registerDebugViews() {
        new ConsoleDebugView();
        new MessageBusDebugView();
        new LightDebugView();
        new MusicDebugView();
        new AccelerometerDebugView();
        new ButtonDebugView();
        new LightSensorDebugView();
        new MicrophoneDebugView();
        new SwitchDebugView();
        new ThermometerDebugView();
        new TouchDebugView();
        // new PixelDebugView();
        new ControllerDebugView();
    }
}

jacdac.devicemanager.ts:
namespace jacdac {
    export class JDDeviceManager {
        devices: JDDevice[];

        constructor() {
            this.devices = [];
        }

        getDeviceList(): JDDevice[] {
            return this.devices.slice(0);
        }

        getRemoteDevice(device_address: number): JDDevice {
            return this.devices.find((dev) => { return dev.device_address == device_address });
        }

        getRemoteDeviceUnique(device_address: number, udidh: number, udidl: number): JDDevice {
            return this.devices.find((dev) => dev.device_address == device_address && dev.udidh == udidh && dev.udidl == udidl);
        }

        addDevice(controlPacket: JDControlPacket, communicationRate: number): JDDevice {
            let dev = this.getRemoteDeviceUnique(controlPacket.device_address, controlPacket.udidh, controlPacket.udidl);

            if (dev)
                return dev;

            dev = new JDDevice(controlPacket, communicationRate);

            this.devices.push(dev);

            return dev;
        }

        updateDevice(controlPacket: JDControlPacket, communicationRate: number): void {
            const dev = this.getRemoteDeviceUnique(controlPacket.device_address, controlPacket.udidh, controlPacket.udidl);

            if (dev)
                dev.update(controlPacket);
        }

        removeDevice(device: JDDevice): void {
            this.devices = this.devices.filter((dev) => !(dev.device_address == device.device_address && device.udidl == dev.udidl && device.udidh == dev.udidh))
        }
    }
}

jacdac.diagnostics.ts:
namespace jacdac {
    export class JDDiagnostics
    {
        bus_state: number;
        bus_lo_error: number;
        bus_uart_error: number;
        bus_timeout_error: number;
        packets_sent: number;
        packets_received: number;
        packets_dropped: number;
        bus_status: string;

        constructor(buf: Buffer)
        {
            if (!buf || buf.length == 0) {
                this.bus_lo_error = 0
                this.bus_uart_error = 0
                this.bus_timeout_error = 0
                this.packets_sent = 0
                this.packets_received = 0
                this.packets_dropped = 0
                this.bus_state = 0
                this.bus_status = "";
            } else {
                this.bus_state = buf.getNumber(NumberFormat.UInt32LE,0);

                if (this.bus_state == 0)
                    this.bus_status = "connected";
                else
                    this.bus_status = "error";

                this.bus_lo_error = buf.getNumber(NumberFormat.UInt32LE,4);
                this.bus_uart_error = buf.getNumber(NumberFormat.UInt32LE,8);
                this.bus_timeout_error = buf.getNumber(NumberFormat.UInt32LE,12);
                this.packets_sent = buf.getNumber(NumberFormat.UInt32LE,16);
                this.packets_received = buf.getNumber(NumberFormat.UInt32LE,20);
                this.packets_dropped = buf.getNumber(NumberFormat.UInt32LE,24);
            }
        }
    }
}

jacdac.driver.ts:
namespace jacdac {
    //% fixedInstances
    export class Service extends jacdac.JDService {
        public name: string;
        protected supressLog: boolean;
        private _controlData: Buffer;
        private _eventId: number;

        constructor(name: string, mode: JDServiceMode, serviceClass: number, controlDataLength = 0) {
            super(serviceClass, mode)

            this.name = name;
            this._controlData = control.createBuffer(Math.max(0, controlDataLength));
            this.supressLog = false;
            this._eventId = control.allocateNotifyEvent();
        }

        get eventId() {
            return this._eventId;
        }

        get serviceNumber(): number {
            this.start();
            return this.service_number;
        }

        get deviceAddress(): number {
            const d = this.device();
            return d ? d.device_address : -1;
        }

        get deviceName(): string {
            const d = this.device();
            return d ? d.device_name : "";
        }

        get controlData(): Buffer {
            return this._controlData;
        }

        get isStarted(): boolean {
            return jacdac.JACDAC.instance.contains(this);
        }

        addAdvertisementData(): Buffer {
            return this._controlData;
        }

        protected log(text: string) {
            if (!this.supressLog || jacdac.consolePriority < console.minPriority) {
                let dev = jacdac.JACDAC.instance.getDeviceName();
                if (!dev) {
                    const d = this.device;
                    dev = d ? toHex8(d.device_address) : "--";
                }
                console.add(jacdac.consolePriority, `${dev}:${toHex8(this.serviceNumber)}>${this.name}>${text}`);
            }
        }

        protected sendPacket(pkt: Buffer) {
            this.start();
            this.send(pkt);
        }

        /**
         * Registers and starts the driver
         */
        //% blockId=jacdachoststart block="start %service"
        //% group="Services" blockGap=8
        start() {
            jacdac.start();
            if (jacdac.JACDAC.instance.add(this)) {
                this.log("start");
            }
        }

        /**
         * Unregister and stops the driver
         */
        //% blockId=jacdachoststop block="stop %service"
        //% group="Services" blockGap=8
        stop() {
            if (jacdac.JACDAC.instance.remove(this)) {
                this.log("stop")
            }
        }
    }

    //% fixedInstances
    export class Broadcast extends Service {
        constructor(name: string, serviceClass: number, controlDataLength?: number) {
            super(name, JDServiceMode.BroadcastHostService, serviceClass, controlDataLength);
        }
    }

    //% fixedInstances
    export class Host extends Service {
        constructor(name: string, serviceClass: number, controlDataLength?: number) {
            super(name, JDServiceMode.HostService, serviceClass, controlDataLength);
        }
    }

    //% fixedInstances
    export class Client extends Service {
        constructor(name: string, serviceClass: number, controlDataLength?: number) {
            super(name, JDServiceMode.ClientService, serviceClass, controlDataLength);
        }

        /**
         * Get the required device names
         */
        get requiredDeviceName() {
            return this.requiredDevice && this.requiredDevice.device_name;
        }

        /** 
         * Set the required device name
        */
        set requiredDeviceName(name: string) {
            const dev = new jacdac.JDRequiredDevice();
            dev.device_name = name;
            this.requiredDevice = dev;
        }

        /**
         * Set required device uuid
         */
        get requiredDeviceUuid() {
            return this.requiredDevice && this.requiredDevice.unique_device_identifier;
        }

        /** 
         * Sets the required device name
        */
        set requiredDeviceUuid(uuid: Buffer) {
            const dev = new jacdac.JDRequiredDevice();
            dev.unique_device_identifier = uuid;
            this.requiredDevice = dev;
        }

        protected registerEvent(value: number, handler: () => void) {
            control.onEvent(this.eventId, value, handler);
            this.start();
        }
    }
}

