#include "pxt.h"
#include "JACDAC.h"
#include "JDPhysicalLayer.h"

namespace jacdac {

class WJDPhysicalLayer {
#ifdef CODAL_JACDAC_WIRE_SERIAL
    CODAL_JACDAC_WIRE_SERIAL sws;
    JDPhysicalLayer phys;
#endif    

  public:
    WJDPhysicalLayer()
#ifdef CODAL_JACDAC_WIRE_SERIAL
        : sws(*LOOKUP_PIN(JACK_TX))
        , phys(sws, *pxt::getJACDACTimer(), LOOKUP_PIN(JACK_BUSLED), LOOKUP_PIN(JACK_COMMLED), (PIN(JACK_BUSLED) & CFG_PIN_CONFIG_ACTIVE_LO) > 0, (PIN(JACK_COMMLED) & CFG_PIN_CONFIG_ACTIVE_LO) > 0)
#endif        
    {
#ifdef CODAL_JACDAC_WIRE_SERIAL
        sws.setBaud(1000000);
#if CONFIG_ENABLED(DEVICE_USB) && CONFIG_ENABLED(DEVICE_JACDAC_DEBUG)
        if (pxt::jacdacDebug)
            pxt::jacdacDebug->setPhysicalLayer(phys);
#endif
#endif
    }

    void send(Buffer b) {
#ifdef CODAL_JACDAC_WIRE_SERIAL
        phys.send((JDPacket*)b->data,NULL,false);
#endif        
    }

    bool isConnected() {
#ifdef CODAL_JACDAC_WIRE_SERIAL
        return phys.isConnected();
#else
        return false;
#endif
    }

    bool isRunning() {
#ifdef CODAL_JACDAC_WIRE_SERIAL
        return phys.isRunning();
#else
        return false;
#endif
    }

    int getId()
    {
#ifdef CODAL_JACDAC_WIRE_SERIAL
        return phys.id;
#else
        return -1;
#endif
    }

    JDPacket* getPacket()
    {
#ifdef CODAL_JACDAC_WIRE_SERIAL
        auto pkt = phys.getPacket();
#if CONFIG_ENABLED(DEVICE_USB) && CONFIG_ENABLED(DEVICE_JACDAC_DEBUG)
        if (pkt && pxt::jacdacDebug)
            pxt::jacdacDebug->handlePacket(pkt);
#endif
        return pkt;
#else
        return NULL;
#endif        
    }

    void start()
    {
#ifdef CODAL_JACDAC_WIRE_SERIAL
        phys.start();
#endif        
    }

    void stop()
    {
#ifdef CODAL_JACDAC_WIRE_SERIAL
        phys.stop();
#endif       
    }

    JDDiagnostics getDiagnostics()
    {
#ifdef CODAL_JACDAC_WIRE_SERIAL
        return phys.getDiagnostics();
#else
        JDDiagnostics d;
        return d;
#endif       
    }
};

SINGLETON_IF_PIN(WJDPhysicalLayer, JACK_TX);

/**
* Gets the physical layer component id
**/
//%
int __physId() {
    auto jd = getWJDPhysicalLayer();
    return jd ? jd->getId() : -1;
}

/**
 * Write a buffer to the jacdac physical layer.
 **/
//%
void __physSendPacket(Buffer buf) {
    auto jd = getWJDPhysicalLayer();
    if (jd)
        jd->send(buf);
}

/**
* Reads a packet from the queue. NULL if queue is empty
**/
//%
Buffer __physGetPacket() {
    auto jd = getWJDPhysicalLayer();
    Buffer buf = NULL;
    if (jd) {
        auto pkt = jd->getPacket();
        if (pkt)
        {
            buf = mkBuffer((uint8_t*)pkt, sizeof(JDPacket));
            free(pkt);
        }
    }

    return buf;
}

/**
 * Returns the connection state of the JACDAC physical layer.
 **/
//%
bool __physIsConnected() {
    auto jd = getWJDPhysicalLayer();
    return jd && jd->isConnected();
}

/**
 * Indicates if the bus is running
 **/
//%
bool __physIsRunning() {
    auto jd = getWJDPhysicalLayer();
    return jd && jd->isRunning();
}

/**
 * Starts the JACDAC physical layer.
 **/
//%
void __physStart()
{
    auto jd = getWJDPhysicalLayer();
    if (jd)
        jd->start();
}


/**
 * Reads the diagnostics struct provided by the physical layer. Returns a buffer or NULL.
 **/
//%
Buffer __physGetDiagnostics() {
    auto jd = getWJDPhysicalLayer();
    Buffer buf = NULL;

    if (jd) {
        JDDiagnostics diagnostics = jd->getDiagnostics();
        buf = mkBuffer((uint8_t*)&diagnostics, sizeof(JDDiagnostics));
    }

    return buf;
}

/**
 * Stops the JACDAC physical layer.
 **/
//%
void __physStop()
{
    auto jd = getWJDPhysicalLayer();
    if (jd)
        jd->stop();
}

}

jacdac.jacdac.ts:
/// <reference path="ifaces.ts"/>

namespace jacdac {
    class JDDummyBus implements JDPhysicalLayer {
        writeBuffer(buf: Buffer) {
        }

        isConnected() {
            return true;
        }
    }

    /**
     * A packet
     */
    export class JACDAC {
        private static _instance: JACDAC;
        private _controlService: JDControlService;
        private _consoleService: JDConsoleService;
        private _bridge: JDService;
        private _services: JDService[];
        private _bus: JDPhysicalLayer;

        private state: boolean;

        onIdentificationRequest: () => void;
        onNameRemotelyChanged: (name: string) => void;

        get bus() {
            return this._bus;
        }

        set bus(bus: JDPhysicalLayer) {
            this._bus = bus;
        }

        get services(): JDService[] {
            return this._services.slice(0);
        }

        get bridge() {
            return this._bridge;
        }

        set bridge(bridge: JDService) {
            this._bridge = bridge;
        }

        get devices(): JDDevice[] {
            return this._controlService.deviceManager.getDeviceList();
        }

        get controlService() {
            return this._controlService;
        }

        get consoleService() {
            if (!this._consoleService) {
                this._consoleService = new JDConsoleService();
                this.add(this._consoleService);
            }
            return this._consoleService;
        }

        private constructor() {
            this._bus = new JDDummyBus();
            this._services = [];
            this._controlService = new JDControlService();
        }

        setDeviceAddress(device_address: number) {
            this._controlService.disconnect();
            this._controlService.enumerate(device_address)
        }

        //packet received instead?
        routePacket(pkt: JDPacket): void {
            this._controlService.routePacket(pkt);

            // if we have a bridge service, route all packets to it.
            if (this._bridge)
                this._bridge.handlePacket(pkt);
        }

        start(device_address?:number): void {
            if (this.state)
                return;

            this.state = true;
            this._controlService.enumerate(device_address);
        }

        stop(): void {
            if (!this.state)
                return;

            this.state = false;
            this._controlService.disconnect();
        }

        setDeviceName(name: string): void {
            this._controlService.setDeviceName(name);
        }

        getDeviceName(): string {
            return this._controlService.getDeviceName();
        }

        triggerRemoteIdentification(device_address: number): void {
            this._controlService.configurationService.triggerRemoteIdentification(device_address);
        }

        setRemoteDeviceName(device_address: number, name: string): void {
            this._controlService.configurationService.setRemoteDeviceName(device_address, name);
        }

        getRemoteDevice(device_address: number): JDDevice {
            return this._controlService.getRemoteDevice(device_address);
        }

        isRunning(): boolean {
            return this.state
        }

        add(service: JDService): boolean {

            for (let s of this._services)
                if (s == service)
                    return false;

            this._services.push(service);
            jacdac.options.log(`added service cls:${service.service_class}, md:${service.mode} (${this._services.length} services)`);

            // only enumerate if a new host service is added. Enumerate is idempotent.
            if (service.mode == JDServiceMode.HostService || service.mode == JDServiceMode.BroadcastHostService)
                this._controlService.enumerate();

            return true;
        }

        remove(service: JDService): boolean {
            for (let i = 0; i < this._services.length; ++i) {
                if (this._services[i] == service) {
                    jacdac.options.log(`removing service ${service.service_class}`);
                    const isHost = (service.mode == JDServiceMode.HostService || service.mode == JDServiceMode.BroadcastHostService);
                    if (isHost)
                        this._controlService.disconnect();
                    this._services.splice(i, 1);
                    if (isHost)
                        this._controlService.enumerate();
                    return true;
                }
            }

            return false;
        }

        contains(service: JDService): boolean {
            for (let i = 0; i < this._services.length; ++i) {
                if (this._services[i] == service)
                    return true;
            }
            return false;
        }

        public static get instance() {
            return this._instance || (this._instance = new JACDAC());
        }

        write(buf: Buffer, service_number: number, device_address: number, device: JDDevice) {
            let packet = new JDPacket();

            if (device_address == jacdac.JD_CONTROL_TRANSMIT_ONLY_ADDRESS)
                packet.data = device.udid.concat(buf);
            else
                packet.data = buf;

            packet.device_address = device_address;
            packet.service_number = service_number;
            packet.communication_rate = JD_DEVICE_DEFAULT_COMMUNICATION_RATE;

            if (device)
                packet.communication_rate = device.communication_rate;

            packet.crc = jd_crc(packet, device);


            this.writePacket(packet);
        }

        writePacket(pkt: JDPacket) {
            this._bus.writeBuffer(pkt.getBuffer());
        }
    }

    /**
     * Gets the JACDAC instance
     */
    export function instance() {
        return jacdac.JACDAC.instance;
    }

    /**
     * Gets a snapshot of the device list
     */
    export function devices(): JDDevice[] {
        return jacdac.JACDAC.instance.devices;
    }

    /**
     * Gets a value that indicates if the JACDAC bus is connected.
     */
    export function isConnected(): boolean {
        return jacdac.JACDAC.instance.bus.isConnected()
            && jacdac.JACDAC.instance.controlService.isConnected();
    }

    /**
     * Gets a value that indicates if JACDAC is running.
     */
    export function isRunning(): boolean {
        return jacdac.JACDAC.instance.isRunning();
    }

    /**
     * Starts and returns the console service
     */
    export function consoleService(): JDConsoleService {
        return jacdac.JACDAC.instance.consoleService;
    }
}

jacdac.jacdacidentification.ts:
namespace jacdac {
    export class JDIdentification {
        identifying: boolean;

        constructor() {
            this.identifying = false;
            control.onEvent(33,2, () => {
                this.identify();
            });
        }

        identify() {
            this.identifying = true;
            // do something to identify the device
            this.identifying = false;
        }
    }

    export class JDGPIOIdentification extends JDIdentification {
        pin: DigitalInOutPin
        constructor(pin: DigitalInOutPin) {
            super();
            this.pin = pin || pins.pinByCfg(DAL.CFG_PIN_LED);
        }

        identify() {
            if (this.identifying || !this.pin)
                return;

            this.identifying = true;

            let state = false;
            for (let i = 0; i < 50; i++) {
                this.pin.digitalWrite(state = !state);
                pause(100);
            }

            this.identifying = false;
        }
    }

    /**
     * The identitifiation service
     */
    //% fixedInstance whenUsed
    export const identification: JDIdentification = new JDGPIOIdentification(undefined);
}

jacdac.makecodejacdac.ts:
enum JDBusState {
    Receiving,
    Transmitting,
    High,
    Low,
    NotSupported = -1
}

namespace jacdac {

    class JACDACBus implements jacdac.JDPhysicalLayer {
        constructor() {
        }

        start() {
            __physStart();
            control.internalOnEvent(__physId(), DAL.JD_SERIAL_EVT_DATA_READY, () => this.handlePacketData());
        }

        stop() {
            __physStop();
        }

        handlePacketData() {
            let buf: Buffer = undefined;
            while (buf = __physGetPacket()) {
                const pkt = new jacdac.JDPacket(buf);
                jacdac.JACDAC.instance.routePacket(pkt)
            }
        }

        writeBuffer(b: Buffer) {
            __physSendPacket(b);
        }

        isConnected() {
            return __physIsConnected()
        }

        isRunning() {
            return __physIsRunning();
        }

        getDiagnostics() {
            return __physGetDiagnostics();
        }
    }

    let jacdacStarted = false;
    let bus: JACDACBus;
    export function start(): void {
        if (jacdacStarted)
            return;

        jacdacStarted = true;
        if (!bus) {
            bus = new JACDACBus();
            jacdac.JACDAC.instance.bus = bus;
        }

        // load name from storage
        const DEVICE_NAME_STORAGE_KEY = "jddn"

        if (!jacdac.JACDAC.instance.getDeviceName()) {
            const deviceName = configStorage.getItem(DEVICE_NAME_STORAGE_KEY);
            if (deviceName) {
                console.add(jacdac.consolePriority, `:jd> restoring device name changed to ${deviceName}`)
                jacdac.JACDAC.instance.setDeviceName(deviceName);
            }
        }
        jacdac.JACDAC.instance.onNameRemotelyChanged = function (name: string) {
            control.dmesg(`:jd> device name changed to ${name}`);
            console.add(jacdac.consolePriority, `:jd> device name changed to ${name}`)
            configStorage.setItem(DEVICE_NAME_STORAGE_KEY, name);
        };

        jacdac.JACDAC.instance.onIdentificationRequest = function () {
            // DEVICE_ID_JACDAC_CONFIGURATION_SERVICE = 33
            // JD_CONTROL_CONFIGURATION_EVT_IDENTIFY = 2
            control.raiseEvent(33, 2)
        }

        // start service
        bus.start();
        jacdac.JACDAC.instance.start();

        console.addListener(function (pri, msg) {
            jacdac.JACDAC.instance.consoleService.add(<jacdac.JDConsolePriority><number>pri, msg);
        });
    }

    export function diagnostics(): jacdac.JDDiagnostics {
        if (!bus)
            return new jacdac.JDDiagnostics(control.createBuffer(0));

        return new jacdac.JDDiagnostics(bus.getDiagnostics());
    }

    export function stop() {
        if (jacdacStarted) {
            jacdacStarted = false;
            jacdac.JACDAC.instance.stop();
            bus.stop();
        }
    }

    /**
     * Sets the name of the current device on the JACDAC bus
     * @param name name of the device
     */
    //% blockId=jdsetdevicename block="jacdac set device name to $name"
    //% group="Services" blockGap=8
    export function setDeviceName(name: string) {
        jacdac.JACDAC.instance.setDeviceName(name);        
    }
}

