jacdac-services.sensorclient.ts:
namespace jacdac {
    //% fixedInstances
    //% weight=1
    export class SensorClient extends Client {
        // virtual mode only
        protected _localTime: number;
        protected _lastState: Buffer;
        private _stateChangedHandler: () => void;

        private _sensorState: SensorState;

        constructor(name: string, deviceClass: number) {
            super(name, deviceClass);
            this._lastState = control.createBuffer(0);
            this._sensorState = SensorState.None;
        }

        public get state() {
            this.start();
            return this._lastState;
        }

        /**
         * Enables or disable streaming the sensor internal state
         * @param on streaming enabled
         */
        //% blockid=jacdacsensorstreaming block="jacdac %sensor set streaming %on"
        //% on.shadow=toggleOnOff weight=1
        //% group="Services"
        public setStreaming(on: boolean) {
            this.start();
            this._sensorState = on ? SensorState.Streaming : SensorState.Stopped;
            this.sync();
        }

        /**
         * Requests the sensor to calibrate
         */
        public calibrate() {
            this.start();
            const buf = control.createBuffer(2);
            const cmd = SensorCommand.Calibrate;
            buf.setNumber(NumberFormat.UInt8LE, 0, cmd);
            this.sendPacket(buf);
        }

        private sync() {
            if (this._sensorState == SensorState.None) return;

            const buf = control.createBuffer(2);
            const cmd = (this._sensorState & SensorState.Streaming)
                ? SensorCommand.StartStream : SensorCommand.StopStream;
            buf.setNumber(NumberFormat.UInt8LE, 0, cmd);
            this.sendPacket(buf);
        }

        public onStateChanged(handler: () => void) {
            this._stateChangedHandler = handler;
            this.start();
        }

        handleServiceInformation(device: JDDevice, serviceInfo: JDServiceInformation): number {
            if (this._sensorState == SensorState.None) return DEVICE_OK;
            const data = serviceInfo.data;
            const state = data.getNumber(NumberFormat.UInt8LE, 1);
            if ((this._sensorState & SensorState.Streaming) != (state & SensorState.Streaming))
                this.sync(); // start            
            return DEVICE_OK;
        }

        handlePacket(packet: JDPacket): number {
            const data = packet.data;
            const command = data.getNumber(NumberFormat.UInt8LE, 0);
            this.log(`vpkt ${command}`)
            switch (command) {
                case SensorCommand.State: {
                    const state = data.slice(1);
                    const changed = !jacdac.bufferEqual(this._lastState, state);
                    const r = this.handleVirtualState(state);
                    this._lastState = state;
                    this._localTime = control.millis();
                    if (changed && this._stateChangedHandler)
                        this._stateChangedHandler();
                    return r;
                }
                case SensorCommand.Event: {
                    const value = data.getNumber(NumberFormat.UInt16LE, 1);
                    control.raiseEvent(this.eventId, value);
                    return jacdac.DEVICE_OK;
                }
                default:
                    return this.handleCustomCommand(command, packet);
            }
        }

        protected handleCustomCommand(command: number, pkt: JDPacket): number {
            return jacdac.DEVICE_OK;
        }

        protected handleVirtualState(state: Buffer): number {
            return jacdac.DEVICE_OK;
        }

        protected setThreshold(low: boolean, value: number) {
            this.start();
            const buf = control.createBuffer(6);
            const cmd = low ? SensorCommand.LowThreshold : SensorCommand.HighThreshold;
            buf.setNumber(NumberFormat.UInt8LE, 0, cmd);
            buf.setNumber(NumberFormat.Int32LE, 1, value);
            this.sendPacket(buf);
        }
    }
}

jacdac-services.servoclient.ts:
namespace jacdac {
    //% fixedInstances
    export class ServoClient extends ActuatorClient {
        constructor(name: string) {
            super(name, jacdac.SERVO_DEVICE_CLASS, 5);
        }

        /**
         * Set the servo angle
         */
        //% group="Servos"
        //% weight=100
        //% blockId=jdservoservosetangle block="jacdac set %servo angle to %degrees=protractorPicker "
        //% degrees.defl=90
        //% servo.fieldEditor="gridpicker"
        //% servo.fieldOptions.width=220
        //% servo.fieldOptions.columns=2
        //% blockGap=8        
        setAngle(degrees: number) {
            if (!this.state[0] || this.state.getNumber(NumberFormat.Int16LE, 1) != degrees) {
                this.state[0] = 1;
                this.state.setNumber(NumberFormat.Int16LE, 1, degrees);
                this.notifyChange();
            }
        }

        /**
         * Set the throttle on a continuous servo
         * @param speed the throttle of the motor from -100% to 100%
         */
        //% group="Servos"
        //% weight=99
        //% blockId=jdservoservorun block="jacdac continuous %servo run at %speed=speedPicker \\%"
        //% servo.fieldEditor="gridpicker"
        //% servo.fieldOptions.width=220
        //% servo.fieldOptions.columns=2
        run(speed: number): void {
            this.setAngle(Math.map(speed, -100, 100, 0, 180));
        }

        /*
         * Set the pulse width to the servo in microseconds
         */
        //% group="Servos"
        //% weight=10 help=servos/set-pulse
        //% blockId=jdservoservosetpulse block="jacdac set %servo pulse to %micros s"
        //% micros.min=500 micros.max=2500
        //% micros.defl=1500
        //% servo.fieldEditor="gridpicker"
        //% servo.fieldOptions.width=220
        //% servo.fieldOptions.columns=2
        //% parts=microservo trackArgs=0
        setPulse(micros: number) {
            micros = micros | 0;
            micros = Math.clamp(500, 2500, micros);
            if (this.state.getNumber(NumberFormat.UInt16LE, 3) != micros) {
                this.state.setNumber(NumberFormat.UInt16LE, 3, micros);
                this.notifyChange();
            }
        }
    }

    //% fixedInstance whenUsed block="servo client"
    export const servoClient = new ServoClient("servo");

    //% fixedInstances
    export class ServosClient extends ActuatorClient {
        constructor(name: string, length: number) {
            super(name, jacdac.SERVOS_DEVICE_CLASS, 4 * length, 2 * length);
        }

        /**
         * Set the servo angle
         */
        //% group="Servos"
        //% weight=100
        //% blockId=jdservoservossetangle block="jacdac set %servo %index angle to %degrees=protractorPicker "
        //% degrees.defl=90
        //% servo.fieldEditor="gridpicker"
        //% servo.fieldOptions.width=220
        //% servo.fieldOptions.columns=2
        //% blockGap=8        
        setAngle(index: number, degrees: number) {
            this.ensureState((index + 1) * 4);
            const k = index * 4;
            this.state.setNumber(NumberFormat.UInt8LE, k, 1);
            this.state.setNumber(NumberFormat.Int16LE, k + 1, degrees);
            this.notifyChange();
        }

        /**
         * Set the throttle on a continuous servo
         * @param speed the throttle of the motor from -100% to 100%
         */
        //% group="Servos"
        //% weight=99
        //% blockId=jdservosservorun block="jacdac continuous %servo %index run at %speed=speedPicker \\%"
        //% servo.fieldEditor="gridpicker"
        //% servo.fieldOptions.width=220
        //% servo.fieldOptions.columns=2
        run(index: number, speed: number): void {
            this.setAngle(index, Math.map(speed, -100, 100, 0, 180));
        }
    }

    //% fixedInstance whenUsed block="servos client"
