namespace jacdac.dbg {

    export const JACDAC_DEBUG_ICON = img`
        . . . . . c . . . . c . . 1 . .
        . . . 1 . . c c c c . . . . . 1
        . f f f f f 5 c c 5 f f f f f .
        . f . . . . c c c c . . . . f .
        . f . 1 . c . . . . c 1 . . f 1
        . f . . . . . . . . . . . . f .
        f f f f f f f . . f f f f f f f
        f 4 4 4 4 4 f . . f 5 5 5 5 5 f
        f 4 f f f 4 f . . f 5 f f f 5 f
        f 4 f d f 4 f . . f 5 f d f 5 f
        f 4 f d f 4 f . . f 5 f d f 5 f
        f 4 f f f 4 f . . f 5 f f f 5 f
        f 4 4 4 4 4 f . . f 5 5 5 5 5 f
        f 4 f 4 f 4 f . . f 5 f 5 f 5 f
        f 4 4 4 4 4 f . . f 5 5 5 5 5 f
        f f f f f f f . . f f f f f f f
        `;

    export const JACDAC_CONSOLE_ICON = img`
        . . . . . 1 . 1 . . . . . 1 . .
        . . . 1 . . . . . 1 . . . . . 1
        . . . . . f f f f f f f f f f .
        . . . . . f . . . . . . . . f .
        . . . 1 . f . 1 . . . 1 . . f 1
        . . . . . f . . . . . . . . f .
        f f f f f f f . . f f f f f f f
        f 4 4 4 4 4 f . . f 5 5 5 5 5 f
        f 4 f f f 4 f . . f 5 f f f 5 f
        f 4 f d f 4 f . . f 5 f d f 5 f
        f 4 f d f 4 f . . f 5 f d f 5 f
        f 4 f f f 4 f . . f 5 f f f 5 f
        f 4 4 4 4 4 f . . f 5 5 5 5 5 f
        f 4 f 4 f 4 f . . f 5 f 5 f 5 f
        f 4 4 4 4 4 f . . f 5 5 5 5 5 f
        f f f f f f f . . f f f f f f f
        `;

    enum Mode {
        Diagnostics,
        Packets,
        Devices
    }

    class DebugMenu {
        private started: boolean;
        private mode: Mode;
        private consoleVisible: boolean;
        private debugFont: image.Font;
        private marginx: number;
        private marginy: number;
        private debuggerService: jacdac.DebuggerService;
        private hideControl: boolean

        constructor() {
            this.debugFont = image.font5;
            this.marginy = 2;
            this.marginx = 4;
            this.mode = Mode.Diagnostics;
            this.hideControl = false;
            this.consoleVisible = game.consoleOverlay.isVisible();
            jacdac.registerDebugViews();
            this.debuggerService = new jacdac.DebuggerService();
            this.debuggerService.paintPacket = (packet: DebuggerStruct) => {
                if (packet.pkt.device_address == 0 && this.hideControl)
                    return;

                if (this.mode == Mode.Packets)
                    console.log(`${packet.pkt.device_address}[${(packet.view) ? packet.view.name : "??"}]: ${(packet.view) ? packet.view.renderPacket(packet.pkt) : packet.pkt.data.toHex()}`)
            }

            this.debuggerService.paintDevices = (devices: JDDevice[]) => {
                if (this.mode == Mode.Devices) {
                    game.consoleOverlay.clear();
                    console.log(`${devices.length} device(s)`)
                    console.log(`-----------------------`);
                    devices.forEach(d => {
                        let serviceString = ""
                        for (let s of d.services) {
                            const view = DebugView.find(s.service_class);
                            serviceString += ((view) ? view.name : s.service_class.toString()) + " ";
                        }

                        console.log(`id: ${d.device_name || d.udid.toHex()}\r\naddress: ${d.device_address}\r\n\tServices\r\n\t----\r\n\t${serviceString}`)
                        console.log(`-----------------------`);
                    });
                    console.log("");
                }
            }
        }

        makeDiagnostic(label:string, value:string, horizontalOffset: number, color: number, barHeight: number)
        {
            const charCount = Math.max(value.length, label.length)
            let size = this.debugFont.charWidth * charCount + this.marginx
            screen.fillRect(horizontalOffset,screen.height - barHeight,size, barHeight,color);

            screen.print(label, (this.marginx / 2) + horizontalOffset, screen.height - barHeight + this.marginy, 1, this.debugFont);
            screen.print(value, (this.marginx / 2) + horizontalOffset, screen.height - (this.debugFont.charHeight + (2 * this.marginy)), 1, this.debugFont);

            horizontalOffset += size + 1

            return horizontalOffset;
        }

        paintDiagnosticsBar(){
            if (this.mode != Mode.Diagnostics)
                return 0;
            const diag = jacdac.diagnostics();
            let background = 1
            if (diag.bus_state)
                background = 2
            const height = 2 * (this.debugFont.charHeight + (2 * this.marginy))
            screen.fillRect(0, screen.height - height, screen.width, height, background);
            const errorCount = diag.bus_lo_error + diag.bus_uart_error + diag.bus_timeout_error;

            let horiz = 1;
            horiz = this.makeDiagnostic("TX", diag.packets_sent.toString(), horiz, 6, height);
            horiz = this.makeDiagnostic("RX", diag.packets_received.toString(), horiz, 7, height);
            horiz = this.makeDiagnostic("DR", diag.packets_dropped.toString(),horiz, 2, height);
            horiz = this.makeDiagnostic("ERR", errorCount.toString(),horiz, 2, height);

            return height
        }

        stop() {
            if (!this.started) return;
            this.started = false;

            game.popScene();
            game.consoleOverlay.setVisible(false);

            controller._setUserEventsEnabled(true);
        }

        start() {
            game.pushScene();
            controller._setUserEventsEnabled(false);
            game.onShade(() => {
                this.paintDiagnosticsBar();
            });
            controller.left.onEvent(SYSTEM_KEY_DOWN, () => {
                this.mode = Mode.Packets;
                game.consoleOverlay.clear();
            })
            controller.right.onEvent(SYSTEM_KEY_DOWN, () => {
                this.mode = Mode.Devices;
                game.consoleOverlay.clear();
            })
            controller.up.onEvent(SYSTEM_KEY_DOWN, () => {
                this.mode = Mode.Diagnostics;
                game.consoleOverlay.clear();
                console.log(`jacdac dashboard`);
                console.log(` LEFT for packets`)
                console.log(` RIGHT for devices`)
                console.log(` B for exit`)
            })
            controller.down.onEvent(SYSTEM_KEY_DOWN, () => {
                this.hideControl = !this.hideControl;
            })
            controller.B.onEvent(SYSTEM_KEY_DOWN, () => {
                // done
                if (_menu) {
                    _menu.stop();
                    _menu = undefined;
                }
            })

            game.consoleOverlay.setVisible(true);
            console.log(`jacdac dashboard`);
            console.log(` LEFT for packets`)
            console.log(` RIGHT for devices`)
            console.log(` UP for diagnostics`)
            console.log(` DOWN to hide control packets`)
            console.log(` B for exit`)
        }
    }

    let _menu: DebugMenu;
    /**
     * Shows a basic debugger interface
     */
    export function show() {
        if (_menu)
            _menu.stop();
        _menu = new DebugMenu();
        _menu.start();
    }

    scene.systemMenu.addEntry(
        () => "jacdac dashboard",
        show, JACDAC_DEBUG_ICON);

    scene.systemMenu.addEntry(
        () => jacdac.consoleService().consoleMode == JDConsoleMode.Listen ? "hide jacdac console" : "show jacdac console",
        () => {
            if (jacdac.consoleService().consoleMode == JDConsoleMode.Listen) {
                game.consoleOverlay.setVisible(false);
                jacdac.consoleService().consoleMode = JDConsoleMode.Off;
            }
            else {
                game.consoleOverlay.setVisible(true);
                jacdac.consoleService().consoleMode = JDConsoleMode.Listen;
                console.log(`listening to jacdac...`);
            }
        },
        JACDAC_CONSOLE_ICON
    );
}

datalogger.datalogger.ts:
enum LogSeparator {
    //% block="tab"
    Tab = 0x09,
    //% block="comma"
    Comma = 0x2c,
    //% block="semicolon"
    Semicolon = 0x3b
};

/**
 * A tiny data logging framework
 */
//% weight=80 color=#00a0a0 icon="" blockGap=8
//% groups='["Data", "Configuration"]'
namespace datalogger {
    export let SEPARATOR = "\t";
    /**
     * A storage for log data
     */
    export class Storage {
        constructor() {
        }
        /**
         * Initializes the storage
         */
        init(): void { }
        /**
         * Appends the headers in log
         */
        appendHeaders(headers: string[]): void { }
        /**
         * Appends a row of data
         */
        appendRow(values: number[]): void { }
        /**
         * Flushes any buffered data
         */
        flush(): void { }
    }

    let _headers: string[] = undefined;
    let _headersWritten: boolean = false;
    let _row: number[] = undefined;
    let _start: number;
    let _storage: Storage;
    let _enabled = true;
    let _samplingInterval = -1;
    let _sampleCount = 0;
    let _lastSampleTime = -1;
    let _console = false;

    function clear() {
        _headers = undefined;
        _row = undefined;
    }

    function initRow() {
        if (!_storage || _row) return;

        if (!_headers) {
            _headers = [];
            _headersWritten = false;
            _start = control.millis();
            _storage.init();
        }
        _row = [];
        _sampleCount = 1;
        _lastSampleTime = control.millis();
        const s = (_lastSampleTime - _start) / 1000;
        addValue("time (s)", s);
    }

    function commitRow() {
        // write row if any data
        if (_row && _row.length > 0 && _storage) {
            // write headers for the first row
            if (!_headersWritten) {
                _storage.appendHeaders(_headers);
                if (_console)
                    console.log(_headers.slice(1, _headers.length).join(', '));
                _headersWritten = true;
            }
            // commit row data
            if (_samplingInterval <= 0 || control.millis() - _lastSampleTime >= _samplingInterval) {
                // average data
                if (_sampleCount > 1) {
                    for(let i = 1; i < _row.length; ++i) {
                        _row[i] /= _sampleCount;
                    }
                }
                // append row
                _storage.appendRow(_row);
                if (_console) {
                    // drop time
                    console.log(_row.slice(1, _row.length).join(','));
                }
                // clear values
                _row = undefined;
                _sampleCount = 1;
                _lastSampleTime = -1;
            } else {
                // don't store the data yet
                _sampleCount++;
            }
        }
    }

    /**
     * Start a new row of data
     */
    //% group="Data"
    //% weight=100
    //% blockId=datalogAddRow block="data logger add row"
    //% help=datalogger/add-row
    export function addRow(): void {
        if (!_enabled || !_storage) return;

        commitRow();
        initRow();
    }

    /**
     * Add a named value to the row of data
     * @param name name of the cell, eg: "x"
     * @param value value of the cell, eg: 0
     */
    //% group="Data"
    //% weight=99
    //% blockId=datalogAddValue block="data logger add %name|=%value"
    //% blockGap=12
    //% help=datalogger/add-value
    export function addValue(name: string, value: number) {
        if (!_row) return;
        // happy path
        if (_headers[_row.length] === name)
            _row.push(value);
        else {
            let i = _headers.indexOf(name);
            if (i < 0) {
                _headers.push(name);
                i = _headers.length - 1;
            }
            _row[i] += value;
        }
    }

    /**
     * 
     * @param storage custom storage solution
     */
    //%
    export function setStorage(storage: Storage) {
        flush();
        _storage = storage;
        clear();
    }

    /**
     * Commits any buffered row to disk
     */
    //%
    export function flush() {
        if (_headers && _storage)
            _storage.flush();
    }

    /**
     * Set the minimum number of milliseconds between rows
     * @param millis milliseconds between each sample, eg: 50
     */
    //% group="Configuration"
    //% blockId=datalogSetSamplingInterval block="set data logger sampling interval to $millis|(ms)"
    //% millis.shadow=timePicker
    //% help=datalogger/set-sample-interval
    export function setSampleInterval(millis: number) {
        _samplingInterval = millis >> 0;
    }

    /**
     * Turn on or off datalogging
     * @param enabled 
     */
    //% group="Configuration"
    //% blockId=datalogEnabled block="data logger $enabled"
    //% enabled.shadow=toggleOnOff
    //% help=datalogger/set-enabled
    export function setEnabled(enabled: boolean) {
        flush();
        _enabled = enabled;
    }

    /**
     * Send the data logger output to the console
     * @param enabled 
     */
    //% group="Configuration"
    //% blockId="datalogConsole" block="data logger to console $enabled"
    //% enabled.shadow=toggleOnOff
    //% help=datalogger/send-to-console
    export function sendToConsole(enabled: boolean) {
        _console = enabled;
    }

    /**
     * Set the character used to separate values in a row.
     * @param separator the value separator character, eg: "\t"
     */
    //% group="Configuration"
    //% blockId="datalogSeparator" block="data logger set separator $separator"
    //% help=datalogger/set-separator
    export function setSeparator(separator: LogSeparator) {
        if (!_enabled) {
            SEPARATOR = String.fromCharCode(separator);
        }
    }
}

