net.controllersocket.ts:
namespace net {

    export const SOCK_STREAM = 1
    export const AF_INET = 2
    export const MAX_PACKET = 4000
    export const TCP_MODE = 0
    export const UDP_MODE = 1
    export const TLS_MODE = 2

    export class ControllerSocket implements net.Socket {
        _buffer: Buffer;
        _socknum: number;
        _timeout: number;
        _closed: boolean;
        _openHandler: () => void;
        _closeHandler: () => void;
        _errorHandler: (msg: string) => void;
        _messageHandler: (data: Buffer) => void;

        /** A simplified implementation of the Python 'socket' class, for connecting
    through an interface to a remote device
 */
        constructor(private controller: Controller, private host: string | Buffer, private port: number, private conntype: number = null) {
            if (this.conntype === null) {
                this.conntype = net.TCP_MODE
            }
            this._buffer = hex``
            this._socknum = this.controller.socket()
            this.setTimeout(0)
        }

        /** Connect the socket to the 'address' (which can be 32bit packed IP or
    a hostname string). 'conntype' is an extra that may indicate SSL or not,
    depending on the underlying interface
*/
        public connect() {
            if (!this.controller.socketConnect(this._socknum, this.host, this.port, this.conntype)) {
                this.error(`failed to connect to ${this.host}`)
                return;
            }

            this._buffer = hex``

            if (this._openHandler)
                this._openHandler();
        }

        /** Send some data to the socket */
        public send(data: string | Buffer) {
            //console.log("sock wr: " + data)
            this.controller.socketWrite(this._socknum, net.dataAsBuffer(data))
        }

        private error(msg: string) {
            if (this._errorHandler)
                this._errorHandler(msg)
        }

        onOpen(handler: () => void): void {
            this._openHandler = handler;
        }
        onClose(handler: () => void): void {
            this._closeHandler = handler;
        }
        onError(handler: (msg: string) => void): void {
            this._errorHandler = handler;
        }
        onMessage(handler: (data: Buffer) => void): void {
            if (this._messageHandler === undefined) {
                control.runInParallel(() => {
                    while (!this._closed) {
                        let buf = this.read()
                        if (buf.length) {
                            if (this._messageHandler)
                                this._messageHandler(buf)
                        } else {
                            pause(200)
                        }
                    }
                })
            }
            this._messageHandler = handler || null;
        }

        /** Attempt to return as many bytes as we can up to but not including '\r\n' */
        public readLine(): string {
            // print("Socket readline")
            let stamp = monotonic()
            while (this._buffer.indexOf(hex`0d0a`) < 0) {
                // there's no line already in there, read some more
                let avail = Math.min(this.controller.socketAvailable(this._socknum), MAX_PACKET)
                if (avail) {
                    this._buffer = this._buffer.concat(this.controller.socketRead(this._socknum, avail))
                } else if (this._timeout > 0 && monotonic() - stamp > this._timeout) {
                    // Make sure to close socket so that we don't exhaust sockets.
                    this.close()
                    control.fail("Didn't receive full response, failing out")
                }

            }
            const pos = this._buffer.indexOf(hex`0d0a`)
            const pref = this._buffer.slice(0, pos)
            this._buffer = this._buffer.slice(pos + 2)
            // print("rd: " + this._buffer.length + " / " + pref.length + " :" + pref.toString())
            return pref.toString()
        }

        /** Read up to 'size' bytes from the socket, this may be buffered internally! If 'size' isnt specified, return everything in the buffer. */
        public read(size: number = 0): Buffer {
            // print("Socket read", size)
            if (size == 0) {
                if (this._buffer.length == 0) {
                    let avail = Math.min(this.controller.socketAvailable(this._socknum), MAX_PACKET)
                    if (avail)
                        this._buffer = this._buffer.concat(this.controller.socketRead(this._socknum, avail))
                }
                let ret = this._buffer
                this._buffer = hex``
                return ret
            }

            let stamp = monotonic()
            let to_read = size - this._buffer.length
            let received = []
            while (to_read > 0) {
                // print("Bytes to read:", to_read)
                let avail = Math.min(this.controller.socketAvailable(this._socknum), MAX_PACKET)
                if (avail) {
                    stamp = monotonic()
                    let recv = this.controller.socketRead(this._socknum, Math.min(to_read, avail))
                    received.push(recv)
                    to_read -= recv.length
                }

                if (this._timeout > 0 && monotonic() - stamp > this._timeout) {
                    break
                }

            }
            // print(received)
            received.unshift(this._buffer)
            this._buffer = pins.concatBuffers(received)
            let ret = null
            if (this._buffer.length == size) {
                ret = this._buffer
                this._buffer = hex``
            } else {
                ret = this._buffer.slice(0, size)
                this._buffer = this._buffer.slice(size)
            }

            return ret
        }

        /** Set the read timeout for sockets, if value is 0 it will block */
        public setTimeout(value: number) {
            this._timeout = value
        }

        /** Close the socket, after reading whatever remains */
        public close() {
            this._closed = true;
            this.controller.socketClose(this._socknum)
            if (this._closeHandler)
                this._closeHandler();
        }
    }
}

net.net.ts:
/**
 * Networking, WiFi, web requests
 */
//% weight=1
//% advanced=true
//% icon="\uf1eb" color="#8446cf"
namespace net {
    /**
     * Default priority of net log messages
     **/
    export let logPriority: ConsolePriority = -1;
    export function log(msg: string) {
        console.add(logPriority, "net:" + msg);
    }
    export function debug(msg: string) {
        if (logPriority > ConsolePriority.Debug)
            console.add(ConsolePriority.Debug, "net:" + msg);
    }
    export function fail(reason: string) {
        net.log(`error: ${reason}`);
        throw reason;
    }

    export function monotonic(): number {
        return control.millis() / 1000.0;
    }

    export class AccessPoint {
        rssi: number;
        encryption: number;
        constructor(public ssid: string) { }
    }

    export interface Socket {
        connect(): void;
        send(data: string | Buffer): void;
        read(contentLength: number): Buffer;
        close(): void;
        onOpen(handler: () => void): void;
        onClose(handler: () => void): void;
        onError(handler: (msg: string) => void): void;
        onMessage(handler: (data: Buffer) => void): void;
        setTimeout(millis: number): void;
        readLine(): string;
    }

    export class Net {
        private _controller: Controller;
        constructor(private factory: () => Controller) {
            Net.instance = this;
            this._controller = undefined; // null failed to initialize
        }

        static instance: Net;

        get controller(): net.Controller {
            if (this._controller === undefined) {
                net.log(`init controller`)
                this._controller = this.factory();
                if (!this._controller) {
                    net.log(`controller not found`)
                    this._controller = null;
                }
            }
            return this._controller;
        }

        /**
         * Scan for APs
         */
        scanNetworks(): net.AccessPoint[] {
            const c = this.controller;
            try {
                return c ? c.scanNetworks() : [];
            } catch (e) {
                console.error("" + e)
                return [];
            }
        }

        createSocket(host: string, port: number, secure: boolean): net.Socket {
            const c = this.controller;
            if (!c) return undefined;
            const socket = new net.ControllerSocket(c, host, port, secure ? net.TLS_MODE : net.TCP_MODE);
            return socket;
        }

        hostByName(host: string): string {
            const c = this.controller;
            if (!c) return undefined;
            const b = this.controller.hostbyName(host);
            if (b) return b.toString();
            return undefined;
        }
    }

    /**
     * Gets the current Net instance
     */
    export function instance(): Net {
        return net.Net.instance;
    }

    const AP_SECRETS_KEY = "wifi";
    /**
     * Gets the map of SSID -> password pairs
     */
    export function knownAccessPoints(): StringMap {
        return settings.deviceSecrets.readSecret(AP_SECRETS_KEY) || {};
    }

    export function updateAccessPoint(ssid: string, password: string) {
        const k: StringMap = {};
        k[ssid] = password;
        settings.deviceSecrets.updateSecret(AP_SECRETS_KEY, k);
    }

    export function clearAccessPoints() {
        settings.deviceSecrets.setSecret(AP_SECRETS_KEY, undefined);
    }
}

net.requests.ts:
namespace net {
    /**
     * Pings a web site
     * @param dest host name
     * @param ttl 
     */
    //% blockId=netping block="net ping $dest"
    export function ping(dest: string, ttl: number = 250): number {
        net.log(`ping ${dest}`);
        const c = net.instance().controller;
        if (!c) return Infinity;
        // don't crash.
        try {
            return c.ping(dest, ttl);
        } catch (e) {
            console.error("" + e)
            return Infinity;
        }
    }

    export class Response {
        _cached: Buffer
        status_code: number
        reason: string
        _read_so_far: number
        headers: StringMap;

        /** 
         * The response from a request, contains all the headers/content 
         */
        constructor(private socket: Socket) {
            this._cached = null
            this.status_code = null
            this.reason = null
            this._read_so_far = 0
            this.headers = {}
        }

        /** 
         * Close, delete and collect the response data 
         */
        public close() {
            if (this.socket) {
                this.socket.close()
                this.socket = null
            }
            this._cached = null
        }

        /** 
         * The HTTP content direct from the socket, as bytes 
         */
        get content() {
            // print("Content length:", content_length)
            if (this._cached === null && this.socket) {
                const content_length = parseInt(this.headers["content-length"]) || 0
                this._cached = this.socket.read(content_length)
                this.socket.close()
                this.socket = null
            }

            // print("Buffer length:", len(self._cached))
            return this._cached
        }

        /** 
         * The HTTP content, encoded into a string according to the HTTP header encoding
        */
        get text() {
            const b = this.content;
            return b ? b.toString() : undefined;
        }

        get json() {
            return JSON.parse(this.text)
        }

        public toString() {
            return `HTTP ${this.status_code}; ${Object.keys(this.headers).length} headers; ${this._cached ? this._cached.length : -1} bytes content`
        }
    }

    export interface RequestOptions {
        data?: string | Buffer;
        json?: any; // will call JSON.stringify()
        headers?: StringMap;
        stream?: boolean;
        timeout?: number; // in ms
    }

    export function dataAsBuffer(data: string | Buffer): Buffer {
        if (data == null)
            return null
        if (typeof data == "string")
            return control.createBufferFromUTF8(data)
        return data
    }

    /*
    >>> "a,b,c,d,e".split(",", 2)
    ['a', 'b', 'c,d,e']
    */
    function pysplit(str: string, sep: string, limit: number) {
        const arr = str.split(sep)
        if (arr.length >= limit) {
            return arr.slice(0, limit).concat([arr.slice(limit).join(sep)])
        } else {
            return arr
        }
    }


    /** Perform an HTTP request to the given url which we will parse to determine
whether to use SSL ('https://') or not. We can also send some provided 'data'
or a json dictionary which we will stringify. 'headers' is optional HTTP headers
sent along. 'stream' will determine if we buffer everything, or whether to only
read only when requested

*/
    export function request(method: string, url: string, options?: RequestOptions): net.Response {
        net.log(`${method} ${url}`);

        if (!net.instance().controller) {
            // no controller
            const r = new net.Response(null);
            r.status_code = 418; // teapot
            r.reason = "net controller not configured";
            return r;
        }

        try {
            return internalRequest(method, url, options);
        } catch (e) {
            const r = new net.Response(null);
            r.status_code = 418; // teapot
            r.reason = "" + e;
            return r;
        }
    }

    function internalRequest(method: string, url: string, options?: RequestOptions): net.Response {
        if (!options) options = {};
        if (!options.headers) {
            options.headers = {}
        }

        const tmp = pysplit(url, "/", 3)
        let proto = tmp[0]
        let host = tmp[2]
        let path = tmp[3] || ""
        // replace spaces in path
        // TODO
        // path = path.replace(" ", "%20")

        let port = 0
        if (proto == "http:") {
            port = 80
        } else if (proto == "https:") {
            port = 443
        } else {
            control.fail("Unsupported protocol: " + proto)
        }

        if (host.indexOf(":") >= 0) {
            const tmp = host.split(":")
            host = tmp[0]
            port = parseInt(tmp[1])
        }

        let ipaddr = net.instance().hostByName(host)

        let sock: Socket;
        if (proto == "https:") {
            // for SSL we need to know the host name
            sock = net.instance().createSocket(host, port, true)
        } else {
            sock = net.instance().createSocket(ipaddr, port, false)
        }
        // our response
        let resp = new Response(sock)
        // socket read timeout
        sock.setTimeout(options.timeout)

        sock.connect();
        sock.send(`${method} /${path} HTTP/1.0\r\n`)

        if (!options.headers["Host"])
            sock.send(`Host: ${host}\r\n`)

        if (!options.headers["User-Agent"])
            sock.send("User-Agent: MakeCode ESP32\r\n")

        // Iterate over keys to avoid tuple alloc
        for (let k of Object.keys(options.headers))
            sock.send(`${k}: ${options.headers[k]}\r\n`)

        if (options.json != null) {
            control.assert(options.data == null, 100)
            options.data = JSON.stringify(options.json)
            sock.send("Content-Type: application/json\r\n")
        }

        let dataBuf = dataAsBuffer(options.data)

        if (dataBuf)
            sock.send(`Content-Length: ${dataBuf.length}\r\n`)

        sock.send("\r\n")
        if (dataBuf)
            sock.send(dataBuf)

        let line = sock.readLine()
        // print(line)
        let line2 = pysplit(line, " ", 2)
        let status = parseInt(line2[1])
        let reason = ""
        if (line2.length > 2) {
            reason = line2[2]
        }

        while (true) {
            line = sock.readLine()
            if (!line || line == "\r\n") {
                break
            }

            // print("**line: ", line)
            const tmp = pysplit(line, ": ", 1)
            let title = tmp[0]
            let content = tmp[1]
            if (title && content) {
                resp.headers[title.toLowerCase()] = content.toLowerCase()
            }
        }

        /*

    elif line.startswith(b"Location:") and not 200 <= status <= 299:
    raise NotImplementedError("Redirects not yet supported")
    */

        if ((resp.headers["transfer-encoding"] || "").indexOf("chunked") >= 0)
            control.fail("not supported chunked encoding")

        resp.status_code = status
        resp.reason = reason
        return resp
    }

    /** 
     * Send HTTP HEAD request 
     **/
    export function head(url: string, options?: RequestOptions) {
        return request("HEAD", url, options)
    }

    /** 
     * Send HTTP GET request 
     **/
    export function get(url: string, options?: RequestOptions) {
        return request("GET", url, options)
    }

    /** 
     * Send HTTP GET request and return text 
     **/
    //% blockId=netgetstring block="get string $url"
    export function getString(url: string, options?: RequestOptions): string {
        return get(url, options).text;
    }

    /** 
     * Send HTTP GET request and return JSON 
     **/
    //% blockId=netgetjson block="get json $url"
    export function getJSON(url: string, options?: RequestOptions): any {
        options = options || {};
        options.headers = options.headers || {};
        options.headers["accept"] = options.headers["accept"] || "application/json";
        return get(url, options).json;
    }

    /** Send HTTP POST request */
    export function post(url: string, options?: RequestOptions) {
        return request("POST", url, options)
    }

    /** Send HTTP PATCH request */
    export function patch(url: string, options?: RequestOptions) {
        return request("PATCH", url, options)
    }

    /** Send HTTP PUT request */
    export function put(url: string, options?: RequestOptions) {
        return request("PUT", url, options)
    }

    /** Send HTTP DELETE request */
    export function del(url: string, options?: RequestOptions) {
        return request("DELETE", url, options)
    }
}

