#include "pxt.h"
#include "RAFFS.h"
#include "GhostFAT.h"

using namespace pxt::raffs;
using namespace codal;

namespace settings {

#if defined(SAMD21)
#define SETTINGS_SIZE (2 * 1024)
#else
#define SETTINGS_SIZE (32 * 1024)
#endif

class WStorage {
  public:
    CODAL_FLASH flash;
    FS fs;
    bool isMounted;

    WStorage()
        : flash(),
#if defined(STM32F4)
          fs(flash, 0x8008000, SETTINGS_SIZE),
#elif defined(SAMD51)
          fs(flash, 512 * 1024 - SETTINGS_SIZE, SETTINGS_SIZE),
#elif defined(SAMD21)
          fs(flash, 256 * 1024 - SETTINGS_SIZE, SETTINGS_SIZE),
#elif defined(NRF52_SERIES)
#define NRF_BOOTLOADER_START *(uint32_t *)0x10001014
          fs(flash,
             128 * 1024 < NRF_BOOTLOADER_START && NRF_BOOTLOADER_START < (uint32_t)flash.totalSize()
                 ? NRF_BOOTLOADER_START - SETTINGS_SIZE
                 : flash.totalSize() - SETTINGS_SIZE,
             SETTINGS_SIZE),
#else
          fs(flash),
#endif
          isMounted(false) {
        fs.minGCSpacing = 10000;
    }
};
SINGLETON(WStorage);

static WStorage *mountedStorage() {
    auto s = getWStorage();
    if (s->fs.tryMount())
        return s;
    s->fs.exists("foobar"); // forces mount and possibly format
    return s;
}

//%
int _set(String key, Buffer data) {
    auto s = mountedStorage();
    return s->fs.write(key->getUTF8Data(), data->data, data->length);
}

//%
int _remove(String key) {
    auto s = mountedStorage();
    return s->fs.remove(key->getUTF8Data());
}

//%
bool _exists(String key) {
    auto s = mountedStorage();
    return s->fs.exists(key->getUTF8Data());
}

//%
Buffer _get(String key) {
    auto s = mountedStorage();
    auto sz = s->fs.read(key->getUTF8Data(), NULL, 0);
    if (sz < 0)
        return NULL;
    auto ret = mkBuffer(NULL, sz);
    registerGCObj(ret);
    s->fs.read(NULL, ret->data, ret->length);
    unregisterGCObj(ret);
    return ret;
}

static bool isSystem(const char *fn) {
    return fn[0] == '#';
}

//%
void _userClean() {
    auto s = mountedStorage();
    DMESG("clearing user files");
    s->fs.forceGC(isSystem);
    // if system files take more than 25% of storage size, we reformat
    // it likely means user code has written some 'system' files
    if (s->fs.freeSize() < 3 * s->fs.totalSize() / 4) {
        s->fs.format();
    }
}

//%
RefCollection *_list(String prefix) {
    auto st = mountedStorage();
    st->fs.dirRewind();
    auto res = Array_::mk();
    registerGCObj(res);

    auto prefData = prefix->getUTF8Data();
    auto prefLen = prefix->getUTF8Size();
    auto wantsInternal = prefData[0] == '#';

    for (;;) {
        auto d = st->fs.dirRead();
        if (!d)
            break;
        if (!wantsInternal && d->name[0] == '#')
            continue;
        if (memcmp(d->name, prefData, prefLen) != 0)
            continue;
        auto str = mkString(d->name, -1);
        registerGCObj(str);
        res->head.push((TValue)str);
        unregisterGCObj(str);
    }
    unregisterGCObj(res);
    return res;
}

} // namespace settings

settings.settings.ts:
namespace settings {
    const RUN_KEY = "#run";
    const SCOPE_KEY = "#scope";
    const DEVICE_SECRETS_KEY = "#secrets";
    const SECRETS_KEY = "__secrets";

    //% shim=pxt::seedAddRandom
    declare function seedAddRandom(n: number): void;

    //% shim=settings::_set
    declare function _set(key: string, data: Buffer): int32;

    //% shim=settings::_remove
    declare function _remove(key: string): int32;

    //% shim=settings::_exists
    declare function _exists(key: string): boolean;

    //% shim=settings::_get
    declare function _get(key: string): Buffer;

    //% shim=settings::_userClean
    declare function _userClean(): void;

    //% shim=settings::_list
    declare function _list(prefix: string): string[];

    export function runNumber() {
        return readNumber(RUN_KEY) || 0
    }

    function setScope(scope: string) {
        if (!scope || scope.length > 100)
            control.panic(922)
        const currScope = readString(SCOPE_KEY)
        if (currScope != scope) {
            _userClean()
            writeString(SCOPE_KEY, scope)
        }
    }

    function initScopes() {
        const rn = runNumber() + 1
        writeNumber(RUN_KEY, rn)

        seedAddRandom(control.deviceSerialNumber() & 0x7fffffff)
        seedAddRandom(rn)

        setScope(control.programName())
    }

    initScopes()

    /** 
     * Delete all non-system settings.
     */
    export function clear(): void {
        _userClean()
    }

    /**
     * Set named setting to a given buffer.
     */
    export function writeBuffer(key: string, value: Buffer) {
        if (_set(key, value)) {
            // if we're out of space, clear user storage
            _userClean()
            // and panic - reset should hopefully recreate needed files
            control.panic(920)
        }
    }

    /**
     * Set named settings to a given string.
     */
    export function writeString(key: string, value: string) {
        writeBuffer(key, control.createBufferFromUTF8(value))
    }

    /**
     * Set named settings to a given number.
     */
    export function writeNumber(key: string, value: number) {
        writeBuffer(key, msgpack.packNumberArray([value]))
    }

    /**
     * Set named settings to a given array of numbers.
     */
    export function writeNumberArray(key: string, value: number[]) {
        writeBuffer(key, msgpack.packNumberArray(value))
    }

    /**
     * Read named setting as a buffer. Returns undefined when setting not found.
     */
    export function readBuffer(key: string) {
        return _get(key)
    }

    /**
     * Read named setting as a string.
     */
    export function readString(key: string) {
        const buf = readBuffer(key)
        if (!buf)
            return undefined
        else
            return buf.toString()
    }

    /**
     * Read named setting as a number.
     */
    export function readNumber(key: string) {
        const buf = readBuffer(key)
        if (!buf)
            return undefined
        else {
            const nums = msgpack.unpackNumberArray(buf)
            if (nums && nums.length >= 1)
                return nums[0]
            return undefined
        }
    }

    /**
     * Read named setting as a number.
     */
    export function readNumberArray(key: string) {
        const buf = readBuffer(key)
        if (!buf)
            return undefined
        else
            return msgpack.unpackNumberArray(buf)
    }

    /**
     * Return a list of settings starting with a given prefix.
     */
    export function list(prefix?: string) {
        if (!prefix) prefix = ""
        return _list(prefix)
    }

    /**
     * Remove named setting.
     */
    export function remove(key: string) {
        _remove(key)
    }

    /**
     * Check if a named setting exists.
     */
    export function exists(key: string) {
        return _exists(key)
    }

    function clone(v: any): any {
        if (v == null) return null
        return JSON.parse(JSON.stringify(v))
    }

    function isKV(v: any) {
        return !!v && typeof v === "object" && !Array.isArray(v)
    }

    function jsonMergeFrom(trg: any, src: any) {
        if (!src) return;
        const keys = Object.keys(src)
        keys.forEach(k => {
            const srck = src[k];
            if (isKV(trg[k]) && isKV(srck))
                jsonMergeFrom(trg[k], srck);
            else
                trg[k] = clone(srck);
        });
    }

    //% fixedInstances
    export class SecretStore {
        constructor(private key: string) { }

        setSecret(name: string, value: any) {
            const secrets = this.readSecrets();
            secrets[name] = value;
            writeString(this.key, JSON.stringify(secrets));
        }

        updateSecret(name: string, value: any) {
            const secrets = this.readSecrets();
            const secret = secrets[name];
            if (secret === undefined)
                secrets[name] = value; 
            else jsonMergeFrom(secret, value);
            const v = JSON.stringify(secrets);
            writeString(this.key, v);
        }

        readSecret(name: string, ensure: boolean = false): any {
            const secrets = this.readSecrets();
            const secret = secrets[name];
            if (ensure && !secret) {
                control.dmesg("missing secret " + name);
                control.panic(control.PXT_PANIC.SETTINGS_SECRET_MISSING);
            }
            return secret;
        }

        clearSecrets() {
            writeString(this.key, "{}");
        }

        readSecrets(): any {
            try {
                const src = readString(this.key) || "{}";
                return JSON.parse(src) || {};
            } catch {
                control.dmesg("invalid secret format")
                return {};
            }
        }
    }

    /**
     * Secrets shared by any program on the device
     */
    //% fixedInstance whenUsed block="device secrets"
    export const deviceSecrets = new SecretStore(DEVICE_SECRETS_KEY);

    /**
     * Program secrets
     */
    //% fixedInstance whenUsed block="program secrets"
    export const programSecrets = new SecretStore(SECRETS_KEY);
}

